INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141761,"Pagi petang orang berjaja","Di bawah sana pohon angsana","Curi tulang lengahkan kerja","Konon rajin amanah di mana?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141762,"Pagi petang pergi mengubah","Anak padi tinggi sehasta","Sungguh malang ditimpa musibah","Sungai kering perigi buta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141763,"Pagi petang ribut berajung","Cempaka biru di dalam puan","Pagi petang duduk termenung","Hati rindu kepada tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141764,"Pagi raya makan ketupat","Ketupat nasi dikerat-kerat","Kita bertanding mencuba bakat","Mesra terjalin bertambah erat", 4, 6);
