INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141766,"Paginya tenang petang bergelora","Perahu nelayan dilambung ombak","Paginya senang petang sengsara","Salah ramalan saham terjebak", 4, 6);
