INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141768,"Pagi-pagi awan berlari","Bintang sa-biji membelah bukit","Kalau dapat gambar di-hati","Dunia di-pandang tinggal sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141769,"Pagi-pagi awannya sejuk","Baik menanam pokok mengkudu","Laki-laki dalam membujuk","Mulutnya manis seperti madu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141770,"Pagi-pagi belayar ke seberang","Sampan kolek mudik ke hulu","Kalau budi dikenang orang","Mengapa dagang terbuang lalau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141771,"Pagi-pagi belom mandi","Ikat rambot tocang Cina","Pi sana pi sini","Nampak macam oghang gila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141772,"Pagi-pagi buat laksa","Tengah hari masak kuah","Oghang benci oghang hina","Sebab kita oghang susah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141773,"Pagi-pagi duduk termenung","Melihat 'nakdara meragi tapai","Hajat hati nak peluk gunung","Apakan daya tangan tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141774,"Pagi-pagi hendak ke ladang","Hendak menyemei si benih padi","Kusangka panas hingga ke petang","Rupanya hujan di tengah hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141775,"Pagi-pagi ka-Langkapuri","Membawa peti ka-pulau bukum","Banyak penchuri perkara penchuri","Penchuri hati tidak di-hukum", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141776,"Pagi-pagi ke Pusat Mengasih","Tempay budak dijaga orang","Kemana lagi ku tumpahkan kasih","Jika tidak tuan seorang (Kumpulan Pantun Sabah)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141777,"Pagi-pagi ke tepi pantai","Pandang ke laot tengak cuaca","Oghang di sini banyak perangai","Kerja ke laot tak tentu masa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141778,"Pagi-pagi kita pun bangkit","Lebah-lebah mengisap madu","Walau pun sa-ribu bintang di langit","Manakah sama si-bulan yang satu!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141779,"Pagi-pagi main lelayang","Encik Sidik bermain sama","Pergilah abang pergilah sayang","Adik sedia mendoakan sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141780,"Pagi-pagi masok ke hutan","Kerja menebang pokok tembusu","Selang sehari carilah kawan","Kerja begitu kawan tak mahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141781,"Pagi-pagi matahari segalah","Burung merpati terbang ke hulu","Sampai di sini saya bermadah","Kalau tak mati berjumpa pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141782,"Pagi-pagi memasak pulut","Untuk bekalan pergi bekerja","Tak sungguh seluang ke laut","Akhirnya balik ke pantai juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141783,"Pagi-pagi memasak pulut","Untuk bekalan pergi bekerja","Tak sungguh seluang melaut","Akhirnya balik ke tepi juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141784,"Pagi-pagi membawa joran","Ikan banyak di dalam paya","Berlagak ranggi tak ikut aturan","Umpama penyangak di jalan raya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141785,"Pagi-pagi memotong getah","Getah dipotong berlari-lari","Apa sebab bayan tergetah","Sebab pitnah si burung nuri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141786,"Pagi-pagi memotong getah","Getah dipotong mengapa lari","Kalau sudah burung bergetah","Ke mana ia hendakkan lari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141787,"Pagi-pagi menampi padi","Padi jambi padi malinja","Hati-hati memberi janji","Mungkir janji badan binasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141788,"Pagi-pagi mengail haruan","Tersangkut duri terkoyak kain","Apa-kah jadi tindakan tuan","Kekaseh lari pada yang lain?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141789,"Pagi-pagi mengambil galah","Hendak dijolok sarang lebah","Lenggok jari bagai nak patah","Menulis juga biar lekas sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141790,"Pagi-pagi menjala gelama","Dapat puput dengan lundu","Kanda pergi jangan-lah badan","Adek ta' sanggup menanggong rindu!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141791,"Pagi-pagi menuai padi","Untuk mengelak panas mentari","Tuan di sana saya di sini","Sebagai penghibur pengganti diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141792,"Pagi-pagi menyiang gelama","Gelama ada di-dalam lokah","Ayah pergi bukan-nya lama","Usah-lah anakanda berhati gundah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141793,"Pagi-pagi naik ke bukit","Bawak air di dalam balang","Hati sendiri juga yang sakit","Pikiran yang baik menjadi hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141794,"Pagi-pagi parang diasah","Takah gambang sekali uji","Jangan tuan berhati susah","Ada temuan mesti jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141795,"Pagi-pagi pergi ke ladang","Hendak menyemai benih padi","Berapa tinggi Gunung Ledang","Tinggi lagi hajat di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141796,"Pagi-pagi pergi ke ladang","Hendak menyemai benih padi","Betapa tinggi Gunung Ledang","Tinggi lagi hajat kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141797,"Pagi-pagi pergi menugal","Menugal padi penuh raga","Kalau kail panjang sejengkal","Jangan laut hendak diduga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141798,"Pagi-pagi petik kecapi","Petik gitar dengan irama","Berhati-hatilah bermain api","Kalau dah besar terbakar semua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141799,"Pagi-pagi turun ke ladang","Hendak menyemai benih padi","Berapa tinggi Gunung Ledang","Tinggi lagi harapan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141800,"Pagi-pagi turun membajak","Janganlah lupa pakai seluar","Jagalah diri jangan terlajak","Takut tak jumpa jalan keluar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141801,"Pagi-pagi unggas melayang","Cuaca indah pantai berseri","Pokok tinggi mengapa bergoyang","Pokok rendah kukuh berdiri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141802,"Pagi-pagi unggas melayang","Melayang ke arah sinaran pelangi","Pokok yang tinggi mengapa bergoyang","Bunga nan merah kenapa tak wangi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141803,"Pagi-pagilah jam enam","Major Winckler membaja nama","Semua ada ditanah lapang","Lantas berjalanlah ke jambatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141804,"Pahala berjemaah lebih tinggi","Di masjid atau di surau","Dengan hati yang sentiasa suci","Sekedar iblis suka buat kacau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141805,"Paham arif balawan banyak","Paham cadiak urang takanai","Paham ciroboh urang tasasak","Paham mati mangunyah bangkai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141806,"Paham arih banyak balawan","Paham mati mangunyah bangkai","Tungku nan tigo sajarangan","Iyolah cadiak tahu pandai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141807,"Paham caia parangai gaduak","Namonyo anak mudo tangguang","Partamo 'gadang manyusuak'","Manyusah urang dalam kampuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141808,"Paham dicancang latiah tanah","Coreng barih pusako tuo","Caro kaampek 'gadang mangulipah'","Hiduik bakarilahan itu namonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141809,"Paham insaf paham nan haniang","Paham sangko cindorong hati","Paham sak tarumbang ambiang","Paham waham malalai diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141810,"Paham nan usah kamalingan","Budiman mati dek katonyo","Tungku nan tigo sajarangan","Partamo cadiak candokio", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141811,"Paham sunyi pikiran nyaman","Nan bana indak ka barubah","Hiduik di dunia bakasiah-kasiahan","Antaro sasamo hambo Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141812,"Paham yakin alemu nyato","Ujuik satu pangana bunta","Haniang itu hulu bicaro","Nanang itu saribu aka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141813,"Pahamlah sekarang engkau berdua","Melepas impian serta khayalan","Dulu status jejaka dan dara","Kini berdua memadu ikatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141814,"Pahar cerek sudah teratur","Cawan mangkuk sudah terhidang","Penganan mulia sudah terletak","Samalah duduk kita makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141815,"Pahat bukan sebanrang pahat","Pahat datang dari Deli","Sahabat bukan sebarang sahabat","Sahabat yang baek susah dicari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141816,"Pahat-pahat dijual orang","Dijual di Pasar Muara","Benar pun jahat dipandang orang","Badan miskin hati mulia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141817,"Pahit bakawali dibuat ubat","Pokok jati jangan ditebang","Patah kaki boleh dibebat","Patah hati lara terbuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141818,"Pahit jangan cepat diludahkan","Manis jangan ditambah lagi","Meski jauh abang berjalan","Karena kasih abang kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141819,"Pahit pahit daun jambu","Lebih pahit daun randu","Memang pahit dimarahi ibu","Lebih pahit menahan rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141820,"Pahit-pahit bunga pepaya","Dalam wajan dicampur minyak","Baik baik hidup tetangga","Sanak jauh kenalan ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141821,"Pahlawan banjir bijak bestari","Membawa tangkal jampi mesra","Kalau banjir melanda negeri","Apa akal apalah bicara?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141822,"Pahlawan muda menikam lembu","Ditikam dengan keris sepukal","Dalam akhirat kita bertemu","Di situ negeri tempat yang kekal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141823,"Pahlawan sebaya tadah perisai","Memanjat kota berhati syukur","Pilihan raya sudah selesai","Rakyat jelata mesti akur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141824,"Pai ka Solok babilalang","Tibo di Solok ilia mudiak","Tangan nan suok takapalang","Tangan nan kida panyalisiak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141825,"Pergi ke Solok menangkap belalang","Tiba di Solok hilir mudik","Tangan kanan terkepalang","Tangan kiri menyelisik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141826,"Pai mandi ka Banda Gadang","Batimbo carano kanso","Jangan baguluik bagolosang","Bagolak golai naknyo lamo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141827,"Pais pelata buat hidangan","Bersayur kundur berulam peria","Pihak swasta diberi sokongan","Bantu yang mundur julang yang jaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141828,"Pait-pait jangan periya","Tekaken sekeq ndeqna maiq","Atengku sedih gitaq iya","Baruq nengke tebilin meriq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141829,"Pajak gadai baharu dibuka","Ramai orang mengadai barang","Sa-andai-nya bagai di-kata","Bukan satu bunga yang kembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141830,"Pak Abu kandaq ayaq","Pagi-pagi juruih pokok","Anak menantu kerjanya besaq","Bangun pagi pakat tengok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141831,"Pak Abu membina titi","Dibina atas tanah Mawardi","Ayah dan ibu si jantung hati","Kasihnya mulia kekal abadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141832,"Pak cakok genggam gamit","Pak sawal hamba raja","Minta cekur beri kunyit","Buat obat ayam buta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141833,"Pak gombak pak gembala","Pak itam jual itik","Anak ikan di kuala","Lumba-lumba tak berisik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141834,"Pak imam makan kenduri","Pak bilal tidak dijemput","Kurus lurus bagai lidi","Lambat mengesut bagai siput", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141835,"Pak kenek-kenek","Masuk lubang batu","Pesan datuk nenek","Simpan kain baju", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141836,"Pak ketipak ketipung","Suare gendang bertalu-talu","Bagaimana mengadu untung","Kalau berdagang rugi melulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141837,"Pak Malau pergi ke Daik","Tanam terombong di bawah batang","Bila teringat budi yang baik","Tidak diseru abang datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141838,"Pak Malau pergi ke Daik","Tanam terombong di bawah batang","Kalau abang berbudi baik","Tidak diseru saya datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141839,"Pak Pandir datang ke kota","Pak Belalang ahli nujum","Gurau sindir bunga senketa","Kerana sayap tetap tersenyum", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141840,"Pak Pandir memakan bubur","Sambil bersembang di pintu kota","Kalau dah banjir kerjanya tidur","Buat kilang si tahi mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141841,"Pak pong Pak Mustape","Pak Dolah di rumahnya","Tepong campur kelapa","Gula merah di tengahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141842,"Pak Pung Pak Mustafa","Encik Dulah di rumahnya","Tepung dengan kelapa","Gula Jawa di tengahnya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141843,"Pak Pung Pak Mustafa","Encik Dulah di rumahnya","Tepung dengan kelapa","Gula Jawa di tengahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141844,"Pak Sutan menjerat landak","Dapat pulak bapak pelanduk","Binatang begitu kalau berkehendak","Disebut namanya katak dan kodok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141845,"Pak tani pulang membajak","Makan sebiji buah kedondong","Kalau tuan orang yang bijak","Apa binatang tandung di hidung?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141846,"Pak tiri kerja selalu","Mak kurang kerja","Pak tiri penat lesu","Mak gelak suka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141847,"Pak Wan Su tokoh Terengganu","Ahli ukir tiada tandingnya","Awan larat ukiran kayu","Seni tua tiada bandingnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141848,"Pak Ya buah melaka","Bakaq belalang kacong","Pak Ya susah kira","Bakaq susah tulong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141849,"Pak Ya main judi","Ketua kampong mengendap","Kerap dok mencuri","Ketua kampong tangkap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141850,"Pakai anak Raja Melayu","Terbang kumbang meniti udara","Bencana banyak si daun kayu","Hati bimbang tidak terkira", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141851,"Pakai apron pergi ke dapur","Masak santank lauk dan kangkung","Angkat pedang boleh bertempur","Musyawarah kembali rukun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141852,"Pakai baju benang suasa","Baju baru benang sutra","Baju Melayu baju merdeka","Dari dulu aku pun suka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141853,"Pakai baju berlapis-lapis","Baju sutera penuh kerawang","Godaan kuat iman menipis","Nipis bagai si kulit bawang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141854,"Pakai baju kancing suasa","Pakai mari Baba Melaka","Biar saya rosak binasa","Kerana dia tidak mengapa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141855,"Pakai baju warna biru","Pergi ke sekolah pukul satu","Murid sentiasa hormatkan guru","Kerana guru pembekal ilmu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141856,"Pakai baju warna biru","Pergi ke sekolah pukul Satu","Hormatilah kita kepada guru","Kenangkan dia setiap waktu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141857,"Pakai chinchin dijari manis","Chabut dikiri buang ka-kanan","Tidor mengimpi bangun menangis","Baru mendusin merasa kangan", 4, 6);
