INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142303,"Parapati tabang jo badan","Tabang maraok masuak rimbo","Bungo satangkai tigo dahan","Tumbuahnyo dalam Lauik Cino", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142304,"Merpati terbang dengan badan","Terbang menyisir masuk rimba","Bunga setangkai tiga dahan","Tumbuhnya dalam Laut Cina", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142305,"Parapati tabang malayang","Inggok di rantiang buah palo","Buah ati tinggallah sayang","Kami baj alan raso lamo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142306,"Merpati terbang melayang","Hinggap di ranting buah pala","Buah hati tinggallah sayang","Kami berjalan serasa akan lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142307,"Paras cantik berbadan molek","Banyak antri para pelamar","Yang terbaik dan terjelek","Tidak bertahan lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142308,"Paras ter-lohor ploh menitik","Turun hujan awan nya biru","Ayam ber-telor chuma s'bijik","Meneran badan ber-kembang kan bulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142309,"Parasang bersarang rumpot","Lebat bulunya panday terbang","Bila sekali angin betiop","Maka boleh layar nya kembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142310,"Pargi kehutan mengumpul rotan","Rotan di rendam di dalam kolam","Jika rumah tak ingin di ganggu syaithan","Bacalah surah Al Baqarah setiap malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142311,"Pari angan dari Melaka","Hijau di gandaria","Cari jangan badan celaka","Biar mati di pangku saya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142312,"Pari di laut Bahar Sailan","Disambar helang dari kayangan","Barang dimakan tiada tertelan","Rasa berbalik di kerongkongan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142313,"Pari menungkup di pasir rata","Mari ditebar dengan jala","Sudah cukup bilangan dunia","Sekali bertiup Sangkakala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142314,"Paria angan derie Melaka","Tjo ie die ganda riea","Tjarie djangan badan tjilaka","Biear mati die pangkoe saija", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142315,"Pariaman banyak perigi","Patah kalam ditimpa dakwat","Perasaan sebulan ini","Rasa di dalam liang lahat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142316,"Pariaman berpagar karang","Perulangan sampan dari Pagai","Angan-angan ke tuan seorang","Sebagai utang belum langsai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142317,"Pariaman jo Ulak Karang","Labuhan sampan rang mangaia","Angan-angan di adiak surang","Sapantun utang tak babayia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142318,"Pariaman manjalo todak","Lalu manjalo ka parahu","Pandai padoman sayo tidak","Budi barubah kami tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142319,"Pariaman menjala todak","Lalu menjala ke perahu","Pandai pedoman saya tidak","Budi berubah kami tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142320,"Pariangan nagari asa","Lareh nan Duo pulo baginyo","Nagara kito ba 'Pancasila'","Sarato Undang-undang dasar 'Ampek Limo'", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142321,"Pari-pari turun mandi","Indah purnama sedang mengambang","Setulus hati sehalus budi","Kasihku Cuma padamu abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142322,"Parit perintar khabarnya susur","Jalan moto Haji Razali","Pesan Hang Tuah berdengan Melur","Biar bercinta sampei ke mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142323,"Parit Perupok Parit Sakai","Sama tengah Parit Keroma","Apa direbut bunga setangkai","Belum tau siapa empunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142324,"Parit Raja ke Batu Pahat","Sampai ke sana membeli gula","Penat bekerja pergilah rehat","Supaya kekuatan pulih semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142325,"Pariuk bareh alah tajarang","Tajarang di tungku tigo","Samangkuak aia lah sadang","Sakarek kayu lah pado", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142326,"Parkaro miliak pulo ditambah","Asa mulo tanah bapunyo","Adapun miliak tantangan tanah","Tabagi ampek parakaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142327,"Parmato jatuah ka rumpuik","Jatuah ka rumpuik ilalang","Di mato sungguahlah lupuik","Di ati tidak kunjuang ilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142328,"Parsuling ni Jatulila","Mariot-iot boti lingon-lingon tarbege","Pupu do nian ita marsapa-sapa","Kiong dohot baro-baro tudia kehe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142329,"Partamo bakato-kato dangan kabajikan","Bicaro dahulu kudian mangecek","Itulah urang arih budiman","Batutua sopan bakato lambek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142330,"Partamo cadiak dakwa jawab","Banguno dalam parakaro","Ado tanyo tarasiang jawab","Bapangtang alah basilek kato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142331,"Partamo cupak usali","Kaduo cupak buatan","Kalau dahulu disasali","Manjadi tuah kamudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142332,"Partamo hiduik di bumi","Kasih ka pangkua jo tambilang","Suko basawah jo batani","Mamaliharo taranak sampainyo kambang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142333,"Partamo undang-undang nagai","Pakaian sagalo urang tuo","Manyusun adat banamo kawi","Kaamanan kampuang jo rumah tanggo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142334,"Paruik dunia ragu jo bimbang","Hatinyo akak tak samporono","Ampadu dunia bangih jo berang","Jantuangnyo suko aniayo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142335,"Parut halus kelapa sebiji","Dapat santan dibuang hampas","Cinta keramat bagai dijanji","Burung disangka boleh terlepas", 4, 6);
