INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142339,"Pasa Mapun Batu Batindieh","Tampak nan dari Ampang Gadang","Kami sapantun aie didieh","Nasi masak badan tabuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142340,"Pasair Panjang tana nya rata","Nampak dekat Blakang Mati","Sungu jauh deri mata","Tetapi tidak deri hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142341,"Pasak berasal di negeri Ranggun","Dibeli saudagar orang Belanda","Di dalam tidur terkejut bangun","Rasa dipeluk tuan adinda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142342,"Pasang aing menganak-anak","Pasang mengalu bulan purnama","Kerbau lalang hampirkan jinak","Mari menulus luaian lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142343,"Pasang aing Tanjung Simasta","Pasangnya tidak bertenang lagi","Lama adinda tidak di mata","Makanku tidak berkenyang lagi", 4, 6);
