INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142356,"Pasang bubu di bawah titi","Pergi berteman memungut kerang","Nasihat ibu tersemat di hati","Jadi pedoman di rantau orang", 4, 6);
