INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142358,"Pasang bubu menangguk udang","Dapat udang terus digoreng","Bukan lembu hendak dikandang","Jiwa remaja minta dibimbing", 4, 6);
