INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142360,"Pasang dian di dinihari","Bersama Sharifah asyik berbeka","Kalau demikian pengakuan diberi","Alamat menempah tiket ke neraka", 4, 6);
