INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142375,"Pasang jerat di waktu malam","Jerat dibawa pergi berburu","Kasih tersurat hati di dalam","Siang dan malam menanggung rindu", 4, 6);
