INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142377,"Pasang jermal menangkap kelah","Naik rakit mengail buntal","Ikhlas beramal disukai Allah","Biar sedikit tetapi kekal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142378,"Pasang joran di hujung galah","Bila mengena tarik perlahan","Baca Quran biarlah molek","Jangan samakan dengan nyanyian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142379,"Pasang joran menahan lukah","Menahan lukah tanpa umpamanya","Perintah Quran jangan disanggah","Kalau disanggah besar dosanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142380,"Pasang kandil di tengah rumah","Terangnya sampai ke tengah halaman","Orang yang dil diberkahi Allah","Orang yang benar dirahmati Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142381,"Pasang katil dengan kelambu","Lurus tiang dengan tilamnya","Orang yang adil tanda berilmu","Akalnya benar elok fahamnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142382,"Pasang kelambu setinggi bahu","Takut digigit nyamuk di hutan","Tahap ilmua manusia tahu","Air setitik di tengah lautan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142383,"Pasang kelambu tepi jendela","Supaya senang pintu dikunci","Biar seribu dara dan janda","Saya memilih yang baik budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142384,"Pasang langsir di malam raya","Syawal menjelang sudah diduga","Untung berganda bertambah kaya","Kaya budi bertambah juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142385,"Pasang lilin bakar kertas","Datok ada di kebun China","Lak-s pama sahya payong kertas","Hujan dan panas baru ter-guna", 4, 6);
