INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142389,"Pasang luka laot Tanjong","Dapat sekor ketam batu","Bijak tuan bijak ter-langsong","Hintan maknikam di kata kan batu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142390,"Pasang lukah dalam Bandar","Lukah hilir sendirinya","Tidak pulang bulan safar","Talak gugur sendirinya", 4, 6);
