INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142572,"Patahlah sayap kembang lelan","Patah ditimpa selaranya","Payahlah mata memandang bulan","Bulan pabila akan jatuhnya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142573,"Patang hari pulang di sawah","Manjinjieng nyiru jo tampian","Lieklah rupo jarieng lawah","Baitu kusuik paratian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142574,"Patang-patang pai mangayia","Batang sulasiah rang ampaikan","Pandai ajo baminyak aia","Adiak kasiah ajo tinggalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142575,"Pataq reket leq loang Tune","Kadu pinaang Raden lupis","Jagaq diriq gamaq nune","Leman pese'an setan iblis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142576,"Patata ikan petutu","Ikan sepat di sawa padi","Tuan berkata biar lah tentu","Sepaya sedap didalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142577,"Patata ikan petutu","Ikan sepat di sawa padi","Tuan berkata biar lah tentu","Sepaya sedap di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142578,"Patin pula ke patin","Sehari-hari merendang lada","Kalau tidak bukit mendinding","Sehari-hari nampak jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142579,"Patitieh pamenan andai","Gurindam pamenan kato","Pangulu kalau tak pandai","Nagari caie kampuang binaso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142580,"Patitih pamenan andai","Gurindam pamenan kato","Jadi pemimpin kok tak pandai","Rusak kampuang binaso koto", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142581,"Patitih pemanan andai","Gurindam pamenan kato","Jadi pemimpin kok tak pandai","Rusak kampuang binaso koto", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142582,"Pattong pattong la daon pandan","Mare bersoemping boenga bidada","Djieka koe ingat kapada toean","Sebagie piso tiekam di dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142583,"Patuh Melayu ke datuk nenek","Orang yang tua dimuliakan","Senonoh laku bicara molek","Barang kemana orang tuakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142584,"Patuh Melayu kepada amanah","Amanah dijaga hidup dan mati","Senonoh laku terpelihara sumpah","Sumpah bertahta di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142585,"Patuh Melayu kepada ayah","Zuriatnya turun kepada anak","Senonoh laku membawa tuah","Sifat penyantun marwahnya tegak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142586,"Patuh Melayu kepada bapak","Bapak alim anak bertuah","Senonoh laku sempurna tegak","Bagaikan kayu banyak berbuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142587,"Patuh Melayu kepada emak","Dari kecil ia besarkan","Senonoh laku mulia akhlak","Hati yang batil ia hindarkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142588,"Patuh Melayu kepada ibu","Syurga ada di bawah kakinya","Senonoh laku tanda berilmu","Bahagia hidup hingga matinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142589,"Patuh Melayu kepada pemimpin","Pemimpn sejati sama disanjung","Senonoh laku terpelihara kain","Batin berisi lahir beruntung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142590,"Patuh Melayu kepada raja","Raja adil raja disembah","Senonoh laku sempurna kerja","Kerja kecil membawa berkah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142591,"Patuh Melayu kepada sunnah","Sunnah Nabi pembawa ramat","Senonoh laku sempurna lidah","Hidup mati pahalanya dapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142592,"Patuh Melayu kepada syarak","Syarak berisi Quran dan sunnah","Senonoh laku takahnya nampak","Duduk berdiri dirahmati Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142593,"Patuh Melayu kepada Ulama","Ulama terpuji lahir dan batin","Senonoh laku mulia nama","Kemana pergi hidup terjamin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142594,"Patuh Melayu kepada yang hak","Bila batil diketepikan","Senonoh laku kokohlah tegak","Besar kecil jadi teladan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142595,"Patuh Melayu mengikut firman","Firman Allah yang maha besar","Senonoh laku berpatut iman","Iman dan taqwa menjadi pagar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142596,"Patung dimain bertali rambut","Rambut dikembar tali sutera","Kepada yang lain jangan disebut","Jika disebut jadi cedera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142597,"Patung kayu baru diukir","Simpan sebagai tanda mata","Penyair menjadi pemikir","Dalam menyusun kata kata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142598,"Patung main bertali rambut","Rambut dikembar tali sutera","Pada yang lain jangan disebut","Jika disebut jadi cedera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142599,"Patut dicari batang putat","Tak terbilang banyak buahnya","Patut dipuji pemegang adat","Tak terbilang banyak petuahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142600,"Patut sungguh sekali-kali","Pada segala orang melihat","Begitu juga sehari-hari","Segala minuman ada sedia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142601,"Patutlah banyak batang buluh","Sebab rumput dituai mati","Patutlah banyak orang terbunuh","Sebab berebut tangkai hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142602,"Pau berinti serikaya","Bawa bekal pergi berjaja","Anak sekolah isteri bekerja","Tinggal pesan di mesin saja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142603,"Pawang menjampi gunakan tawas","Taruh di piring pengalas cawan","Sudah ku tuntut ilmu Abu Nawas","Harimau betina mudah ditawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142604,"Paya berlumpur orang berpimpin","Singgah tetak beringin layu","Kaya bercampur dengan miskin","Sahabat seperti angin yang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142605,"Paya besar kayu bergelang","Kayu mati dibelit ara","Belum musim kakandakan pulang","Ada sedikit lagi bicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142606,"Paya darat paya mentimun","Daun serbat dalam padi","Ibarat air saya nak minum","Tidak ralat di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142607,"Paya di darat sungai Tarum","Cina membelah kayu api","Adinda ibarat air minum","Sedikit tidak ralat di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142608,"Paya direndah mencari cacing","Tanah basah hujannya lambat","Kalau sudah sedara sedaging","Bila susah mencari kerabat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142609,"Paya jugak menanam padi","Nanas pulak di tanam orang","Paya jugak menabor budi","Mas perak di pandang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142610,"Paya malompat orang bupimpin","Singga munutak bringin layu","Kaya busobat dangan miskin","Sobat subagi angin yang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142611,"Paya melompat orang ber pimpin","Singga menetak bringin layu","Kaya bersobat dengan miskin","Sobat sbagi angin yang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142612,"Paya melompat orang berpimpin","Singga menetak beringin layu","Kaya bersobat dengan miskin","Sobat sebagi angina yang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142613,"Paya Terubong di Ayer Itam","Ke rumah To' Kong kita sampai","Jika rajin kuat azam","Apa di cita dapat dicapai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142614,"Payah dimamah lalu digigit","Daging liat dipotong-potong","Mas kahwinnya ribuan ringgit","Puas berjimat jenuh menabung!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142615,"Payah kami bertanam padi","Nanas juga ditanam orang","Payah kami menabur budi","Emas juga dipandang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142616,"Payah kami bertanam padi","Nanas juga ditanam orang","Payah kami menabur budi","Emas juga dipandang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142617,"Payah sungguh bermain silat","Kerana guru terlalu keras","Sudah berpuluh ku kirim surat","Tiada satu yang adik balas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142618,"Payah sungguh membawa perahu","Hujan deras ombak menggila","Payah sungguh hamba merayu","Puan berkeras menolak hamba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142619,"Payah sungguh orang menekat","Dibulak balik tiada berhenti","Payah sungguh abang memikat","Jinak adik jinak merpati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142620,"Payahlah kami nan babansi","Nan barabab di bawah batang","Payahlah kami nan mananti","Apa sababnyo lambek datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142621,"Payahlah kami nan berbangsi","Berebab di bawah batang","Payahlah kami nan menanti","Apa sebabnya lambat datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142622,"Payokumbuah baladang kunik","Dibao nak urang ka Kuantan","Indak namuah kuiang dek kunik","Bapantang lamak dek santan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142623,"Payokumbuah baladang kunyik","Dibawo nak rang ka Kuantan","Tak namuah kuning dek kunyik","Bapantang lamak dek santan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142624,"Payokumbuah jalannyo luruih","Singgah sabanta di Piladang","Kok gantiang jan sampai putuih","Tinggakan juo salai banang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142625,"Payra malayu ka negri kling","Di bli serta labor labor-kan","Suda layu ampir nak kring","Nanti kutika nak gugor gugor kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142626,"Paytak ruang berisi kan kayu","Batu karang balas tindayan","Pata tulang diri mengadu","Tentu orang blas kesian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142627,"Payuang lilin bakasau rotan","Pamayuang dayang pai ka Judah","Jawek pakirim sampaikan salam","Katokan sayang balun sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142628,"Payung lilin berkasau rotan","Pemayung dayang pergi ke Jedah","Jawab kiriman sampaikan pesan","Katakan sayang belum sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142629,"Payung bertulis lagi berenda","Payung 'tu zaman dulu kala","Hidangan sedia menanti 'kanda","Tersaji sudah sediakala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142630,"Payung kain bertangkai tanduk","Bertepi renda berpintal benang","Gelak senyum dibawa tunguk","Sebagai tanda belajar kenal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142631,"Pecah belah batu di gunung","Seri dewa berjalan malam","Ya Allah tidak tertanggung","Rasa tidak dikandung alam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142632,"Pecah belah batu digunung","Seri dewa berjalan malam","Ya Allah tidak tertanggung","Rasa tidak dikandung alam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142633,"Pecah benteng dirempuh","Itik angsa berenang di kali","Biar genting jangan putus","Putus asa tidak sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142634,"Pecah buluh air tempayan","Air sekiri pembasuh kaki","Hilang nuri berganti bayan","Bayan seekor penghibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142635,"Pecah buluh aying tempayan","Aying se kiri pembasuh kaki","Hilang nuri berganti bayan","Bayan seekor penghibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142636,"Pecah buyung berisi buah","Reput di petik bunga lalang","Saya umpama si burung tempua","Sebarang rumput di buat sarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142637,"Pecah buyung sebuah dua","Reput dipetik bunga lalang","Saya umpama si burung tempua","Sebarang rumput dibuat sarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142638,"Pecah buyung tempayan ada","Boleh juga menyuci kaki","Terbang tiung bayan ada","Boleh juga mengibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142639,"Pecah buyung tempayan ada","Boleh juga basuh kaki","Terbang burung ayam ada","Boleh juga menghibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142640,"Pecah buyung tempayan ada","Boleh juga mencuci kaki","Terbang tiung bayan ada","Boleh juga menghibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142641,"Pecah buyung tempayan ada","Dapat juga mencuci kaki","Hilang tiung bayan ada","Dapat juga menghibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142642,"Pecah cangkir berganti pinggan","Emas urai dalam gelata","Sejak anak emak tinggalkan","Gila berurai air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142643,"Pecah cangkir cawan pinggan","Emas derail dalam geleta","Sejak anak bonda tinggalkan","Gila berurai air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142644,"Pecah cangkir cawan pinggan","Emas urai dalam geleta","Sejak anak bonda tinggalkan","Gila berurai air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142645,"Pecah cawan di atas peti","Cawan minum Sultan Amat","Tuhan Allah yang Maha Suci","Jangan dilupakan setiap saat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142646,"Pecah cawan ditimpa cawan","Selasih tumbuh di perigi","Berjalan sembilan bulan","Kerana kasih kembali lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142647,"Pecah cawan ditimpa cawan","Pecah ditimpa belimbing","Hati mampu kami tahan","Mata dengan apa kan di dinding", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142648,"Pecah cerek cawan berdengung","Singgah terantuk di kedai kain","Di dalam tilik dengan tenung","Tuan sudah mencari lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142649,"Pecah kaca di muka pintu","Tembikar dia mengajuk kaki","Hati suka berkata begitu","Jiwa hilang tidak peduli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142650,"Pecah kaca di muka pintu","Tembikar dia mengajuk kaki","Hati suka berkata begitu","Jiwa hilang tidak peduli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142651,"Pecah kaca ditimpa bata","Anak Kerukut mandi bersiram","Jika saya sudah bercinta","Tidak takut akan tenggelam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142652,"Pecah kapur Che Malim","Pecah di timpa tudung kudai","Astaghfarrul'llah al-aathin","Yang di simpul sudah terungkai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142653,"Pecah mangkuk pecah cawanku","Tali gasing di bawah rumah","Bukannya patut bukan lawanku","Laksana anjing di bawah rumah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142654,"Pecah mangkuk pecah pinggan","Tali gasing di bawah rumah","Tiada patut tiada padan","Kerising di bawah rumah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142655,"Pecah pekapur che' malim","Pecah di timpa panah petang","Astaghfaru'llah al-aathim!","Pucuk di cita ulam datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142656,"Pecah pekapur Cik Mualim","Pecah ditimpa panah petang","Astagfirullah al-'azim","Pucuk dicita ulam mendatang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142657,"Pecah piring ditimpa cawan","Cawan dihidang di dalam talam","Wajah tuan sangat rupawan","Terbayang-bayang siang dan malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142658,"Pecah piring tertimpa batu","Piring pecah terbelah dua","Biduk dua nahkoda satu","Ibarat orang beristri dua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (142659,"Pecah piring tertimpa batu","Bawa berlayar ke Indragiri","Jangan adik kata begitu","Adik bunga suntingan negeri", 4, 6);
