INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145989,"Ramuan akar di dalam kumbang","Arak campur keras minuman","Ada kegilaan bercintaan","Jelas ada alasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145990,"Ranah Minang aman sentosa","Penuh dengan tubuh-tumbuhan","Jangan dikata bercerai lama","Nan sehari rasa sebulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145991,"Rancak antan menari di lesung","Padi kena kiri dan kanan","Adil sultan titah dijunjung","Bagai purnama menerang taman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145992,"Rancak carano bukan kapalang","Baukia tapi balereng","Puti di mano nan mamacik","Manyonsong tamu nan datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145993,"Rancak menari pasangannya padan","Lenggang lenggok ke kanan ke kiri","Lentik jari lembutnya badan","Diajak menari kenapa lari?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145994,"Rancaknyo pakan dek sudaga","Elok tapian dek nan mudo","Bawa pacah ambalau tingga","Puntiang kok patah di hulunyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145995,"rancang perak rantai suasa","kenaikan anak raja Melayu","orang bijak ingat kan masa","menggunakan masa ianya tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145996,"Ranchak gong bunyi rebana","Orang di-kota bangun menari","Antara belom jadi binchana","Baik kita menarek diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145997,"Ranck tarian namanya joget","Suaranya lunak ramai yang datang","Lazat durian tekak tak sakit","Tidurnya nyenyak tidak berhutang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145998,"Randhane rak randha teles","Nek disenggol gak gelem ndelok","Mula awak wis kadung apes","Nemu arek wedok ko lolak-lolok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (145999,"Rang Bugih pulang ka Bugih","Mam-bao biduak sa-dewa-kan","Tolan sa-pantun juru-tulih","Ma-nyurek tidak ka-liat-an (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146000,"Rang Bukik Putuih pai ka Kamang","Dirandang jaguang digulo-i","Hukum putuih badan tabuang","Dipandang kampuang ditangisi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146001,"Rang Kurai bapikek balam","Lah dapek bari batali","Kok pandai maelo paham","Baru banamo rang jauhari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146002,"Rang Kurai rang Bukiktinggi","Luhak banamo Luhak Agam","Parampuan nan pandai manjago diri","Kunci nan arek biliak nan dalam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146003,"Rang Lasi pai baburu","Dapeklah ruso baling kaki","Padusi kalau tak bamalu","Ibarat kapa tak bakamudi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146004,"Rang Lubuak Aluang ka Pasa Usang","Mambao ragi tapai jo lamang","Manangih badan di rantau urang","Iyo taragak badan nak pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146005,"Rang Minang hiduik dalam pusako","Ado bakorong jo bakampuang","Ado barumah jo batanggo","Batungganai rumah batuo kampuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146006,"Rang mudo gadang galapua","Lagonyo di bawah sajo","Baso basi garan nan hancua","Lauik gadang batapi tido", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146007,"Rang Padang baniago itiak","Lantera namo biduaknyo","Bungo kambang jan diambiak","Barasai ula di tampuaknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146008,"Orang Padang berniaga itik","Lentera nama biduknya","Bunga mekar jangan dipetik","Serasa ada ular di tampuknya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146009,"Rang Padang mandi ka gurun","Mandi ba-kusuak daun-limpato","Ari patang mantari turun","Bimbang ma-urai aia-mato (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146010,"Rang Parik Putuih ka lapau randah","Mambao balam dalam sarangnyo","Kasieh putuih sayang tak sudah","Lauik nan dalam nan dihadangnyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146011,"Rang Payokumbuah ka Batusangka","Singgah sabanta di Barulak","Konon sumando bapak paja","Awak babini balanjo indak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146012,"Rang salo tingga di salonya","Rami galanggang Ampek Angkek","Nan kayo tingga di kayonyo","Nan bansaik maansua basilambek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146013,"Ranggung lantaikanlah di bemban","Padi dan benta punya buah","Tanggung rasakanlah di badan","Hati dan mata punya ulah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146014,"Ranggung lantaikanlah dibamban","Padi dan banto punya ulah","Tanggung rasaikanlah di badan","Hati dan mata punya ulah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146015,"Ranggung rantaiakanlah dibamban","Padi dan banta punya buah","Tanggung rasaikaikanlah di badan","Hati dan mata punya ulah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146016,"Rangkiang sabanja tigo","Bakapuak gadang bakapuak ketek","Kapuak gadang salo manyalo","Kapuak ketek salek manyalek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146017,"Rangup sungguh mentimun muda","Dari hujung sampai ke pangkal","Hilang pegangan tenggelam punca","Usaha tawakal jadikan tangkal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146018,"Rantai emas tali bubut","Pakai sepasang nampak manisnya","Unjuk beri hati terpaut","Wajah cantik baik budinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146019,"Rantai labuh baju belah","Benang emas tekat di kasut","Beralah bukannya kalah","Harga diri nilai tak susut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146020,"Rantai permata ikat perak","Batu topaz kuning warnanya","Pahlawan pulang disanjung diarak","Padan dengan bakti jasanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146021,"Rantau diturut dengan undang","Negeri dihuni dengan lembaga","Kalau menurut agama yang terang","Hidup mati aman sentosa", 4, 6);
