INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146023,"Rantin dahan muda","Ada buah moktan","Timpa tang kelapa","Ayam jadi pengsan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146024,"Ranting berangan melampai ke kota","Kerana patah dipijak","Jangan pula saya di kata","Kerana saya bukannya bijak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146025,"Ranting kemunting dibuat para","Ambil rotan diikat teguh","Kerja penting minta disegera","Masa berlalu usah bertangguh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146026,"Ranting rumenia di hutan dalam","Hutan sultan raja di linggi","Di banding dunia dengan alam","Intan tuan terlalu tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146027,"Ranum bersih buah mengkudu","Perah isi ambil yang pati","Selembar kasih menyambung rindu","Seitik benci menghitam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146028,"Rao-rao sasudah Panti","Bapasie jalan ka Guguak","Di mano sumua digali","Di sinan aie disauak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146029,"Rapat dan pertemuan","Agenda setiap bulan","Jangan menolak perubahan","Itu rahasia kemajuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146030,"Rapat-rapat jangan jarang","Kayunya jangan tinggi dan rendah","Duduk sahaja mengumpat orang","Suatu pun tidak apa faedah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146031,"Rapo manunjuakkan harago","Kurenah manunjuakkan laku","Walau nan lahia tampak dek mato","Nan batin tasimpan dalam itu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146032,"Rapuhnya tat sedapnya kuih","Hancur di mulut dimamah cair","Rapuhnya taat bagaikan buih","Luas laut tidak berair!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146033,"Rarak kalikie dek mindalu","Tumbuah sarumpun jo sikasek","Kok hilang raso jo malu","Bak kayu lungga pangabek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146034,"Rarak kalikih dek binalu","Tumbuah sarumpun di tapi tabek","Kok habih raso jo malu","Bak kayu lungga pangabek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146035,"Rarak kalikih dek bindalu","Tumbuah di hulu tapi tabek","Kok abih raso jo malu","Bak kayu indak bapangabek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146036,"Rarawak daun susupan","Hambawang kadak babigik","Handak merawak terasa sopan","Kalukok orang kadak regek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146037,"Rasa akal daya dan nafsu","Di dalam raga nyata bersatu","AKU meliputi segala liku","Dari ujung rambut hingga ke kuku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146038,"Rasa aman hidup gembira","Makmurlah masyarakat gemilanglah watan","Berada di bawah satu bendera","Taat setia kepada sultan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146039,"Rasa gula manisnya betik","Manis lagi belimbing madu","Adab mata memilih yang cantik","Resam telinga mendengar yang merdu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146040,"Rasa hendak menanggok rasa tidak","Banyak lintah paya dalam","Rasa hendak masuk rasa tidak","Nanti bertitah adik di dalam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146041,"Rasa malan si buah pinang","Yang kelat simpan di peran","Berdebat bukan kerana menang","Cara sihat menegak kebenaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146042,"Rasanya garam air kuala","Talamburang arus tergulung","Kekurangan dari segala","Sebab tiadalah orang tolong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146043,"Rasanya manis bagaikan gula","Dibuat pengat bertambah nikmat","Sengketa habis hilanglah bala","Selamatlah umat dunia akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146044,"Rasanya sedap namanya buruk","Batang buruk hidangan raya","Beras banyak tapi disorok","Helah niaga nak cepat kaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146045,"Rasaupun patah hanyut terapung","Batang mayang dibuang jangan","Walau kita berlainan kampung","Kasih abang dibuang jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146046,"Rasian pamenan lalok","Kacimpuang pamenan mandi","Tiado tabilang lah tahatok","Sambah jo simpuah panjalani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146047,"Rasian permainan lelap","Kecimpung permainan mandi","Kurang terbilang dan terhatap","Sembah dan simpuh menjalani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146048,"Rasio dunia cimburu bakapanjangan","Parhiasannyo banyak harato tak hala","Balakang dunia panuah kabancian","Hadapannyo durhako ka Allah Taala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146049,"Rasulullah didalam sabdanya","Jangan rumahmu di jadikan kubur","Artinya Qur'an tak dibacanya","Sepi lah rumahnya dari As Syaakur", 4, 6);
