INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146052,"Rata rata menchochok pagar","Kachang para dengan bua nya","Tuan kata saya mendengar","Apa perkara akan ka-dua nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146053,"Ratib-ratib bunga tanjung","Bunga kembang di hujung galah","Ratib-ratib sama sekampung","Ratib seorang memuji Allah", 4, 6);
