INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146055,"Ratna suasa permata di peti","Sinaran di cermin bayang warna","Sempena puasa kita hayati","Jadikan mukmin yang sempurna", 4, 6);
