INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146057,"Ratu Anum Panji Kelana","Di seru dewa terbang melayang","Cantik tersenyum sekuntum bunga","Sayang bunga di kawal kumbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146058,"Ratu Daha ampunya isteri","Emas uri ditanah Jawa","Miskin piatu badan sendiri","Umpama seki bersama nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146059,"Ratu kediri keluar berperang","Berdatang sembah semua hulubalang","Hidup seperti kerja sumbang","Masuk tak bertambah keluar tak kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146060,"Ratu Mangkada ke Banjar Sari","Singgah memetik bunga cempaka","Tidak berbeda sehari-hari","Seputar alam tidak terharga", 4, 6);
