INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146067,"Raut buluh diolah sangkar","Handak meangok burung balatok","Amon barat amon pintar","Cuba-cuba gunai akok", 4, 6);
