INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146069,"Raut gula bersagi-sagi","Buat mari sa rampang ikan","Kuasa Allah di bagi-bagi","Besar laut pada daratan", 4, 6);
