INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (146075,"Rawa bangun jalan berliku","Penuh onak makanan budak","Gelak senyum rupa kakakku","Melihat nenek duduk berbeda", 4, 6);
