INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148753,"Si Amin anak di Kedah","Turun memacuk di tengah padang","Doa diminta amin ditadah","Mintakan bulan biar terang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148754,"Si Awang membeli purun","Burung punai terbang ke sawah","Asalnya raja turun-temurun","Berunai sudah negeri bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148755,"Si ayuak si alang tabang","Tarubuak maha balinyo","Tadayuak hati ka nan hilang","Disabuik apo ka gunonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148756,"Si Bandeng nama soeda","Singga di poeloe main layangan","Nonna di banding tidak berbeda","Bri diambil boeat pasangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148757,"Si batu sundur","Si batu parsoli","Na ingot martutur","Na so lupa di nari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148758,"Si berahi menjunjung talam","Direncong berlipat-lipat","Mati berahi anak Adam","Kena doa pandang makrifat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148759,"Si Bonding nama Saida","Singgah di pulau main layangan","Norma di banding tidak berbeda","Baik di ambil buat pasangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148760,"Si burun nuri di tepi meja","Selepas makan jalan menepi","Jawapannya mudah sahaja","Itulah alat pemadam api", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148761,"Si buruong gontar tobang mengirap","Kakinyo panjang malapik sayap","Wahai saudaro ondaklah insap","Ibarat syair pikirkan totap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148762,"Si Culik kotanya rendah","Silaras di pelimbahan","Unggas menggerek masuk tanah","Dua belas tembilang makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148763,"Si Daera namo rang Tiku","Tariek buluah pan-dagang garam","Namun ta sada di ati-ku","Gunuang runtuah mantari padam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148764,"Si Dani upiek rang namokan","Pandai ma-lenggang tangah pakan","Ati ta-dorong bakeh tolan","Sa-katek tidak ka-liat-an (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148765,"Si Dapat pandai menari","Di gaung busut banyak kembili","Patik tidak dapat mencuri","Lalu meratap anak dan bini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148766,"Si dara manja arak berkuda","Dalam perayaan berbaju Sanggam","Masa remaja mesti waspada","Elak godaan pelbagai ragam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148767,"Si Daud anak rang Padang","Badeta batiak barendo","Di lauik bungo nan kambang","Di darek kumbang nan gilo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148768,"Si Daud anak orang Padang","Berdestar batik berenda","Di laut bunga nan kembang","Di darat kumbang menggila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148769,"Si Dauik anak rang Padang","Ba-data batiek ba-rendo","Di lauik bungo nan kambang","Di darek kumbang mang-gilo (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148770,"Si Dega anak rang Padang","Bajua candu kan diisok","Babega buruang nan dek tabang","Mancari kayu tampek inggok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148771,"Si Dega anak rang Padang","Menjual candu kan dihisap","Berbega burung terbang","Mencari kayu tempat hinggap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148772,"Si elang gunong si elang","Tampak dari Indragiri","Sudah nasib menjunjong malang","Tertanggong juga dibadan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148773,"Si Esa anak di Jawa","Bunting sulung sembilan bulan","Kalu rindu hati nyawa","Kalam di lenchong surat kirim kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148774,"Si Etam anak di jawa","Bunting sulong samilan bulan","Tuan sorang guliga jewa","Sarasa dudok bupayong bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148775,"Si gadis Rungus berjalan berpayung","Melangkah jerami jalan di tepi","Menongkah arus bagai pendayung","begitulah kami di DBP", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148776,"Si Geneng anak 'rang Padang","'Lah lalu ke Banahumpu","Sabai nan geneng tengah padang","Tahanlah tembak bersetumpu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148777,"Si gila mengail pari","Kiambang habis dimakan ikan","Gila benar orang itu tadi","Burung terbang diberahikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148778,"Si Gulandak Sutan Bagindo","Kayu bungkuak bari ba-janjang","Kandak kito ba-duo","Karam biduak samo ba-ranang (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148779,"Si gulandang guntiang patanun","Jatuah kaki luko tangan","Kok anggak unggeh janjamun","Lapeh kan buruang ganggo anam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148780,"Si gulandang gunting untuk bertenun","Jatuh kaki luka tangan","Kalau enggan unggas janjamun","Lepaskan burung ganggo enam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148781,"Si Gumarang namonyo kudo","Tapawik di tangah padang","Alek adat duo macamnyo","Alek ketek jo alek gadang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148782,"Si jantu mati di-tandeh","Mati di-tandeh dalam tonggak","Di-tantang pulau tanah Nieh","Samo di-lamun-lamun ombak (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148783,"Si Jono namo rang Jao","Pandai ba-kudo patang ari","Pancarogo bukan sa-buah","Kurang iman pasiek-lah diri (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148784,"Si junjuang pakan-nyo kamih","Ari isnayan di Koto Tujuah","Kek untuang dapek dek kami","Kok tidak apo kan dayo (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148785,"Si Karani upike rang namo-kan","Tingga di rumah atok genteang","Barani adiek mam-bana-kan","Ba-kain putuih di-guntieng (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148786,"Si Kayo namo nak rang Padang","Tingga di rumah Koto Baru","Dapek ba-linduang di kayu gadang","Kayo Allah sia nan tau (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148787,"Si kedidi makan di pantai","Kering pasang makan ke tengah","Baik budi yang kuning tinggi","Diadu kami mari berperang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148788,"Si kejang bunga sepagi","Tetak batang di dalam padi","Fiil bagai anjing dan babi","Tiada berani masuk negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148789,"Si Kilang dari Rumbia","Menggulai kepala todak","Hilang asal dengan mulia","Entahkan dapat entah tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148790,"Si kudidi makan di pantai","Kariang pasang makan ka tangah","B aiak budi nan kuniang lanj ai","Diadu kami parang manah (Van Ophuijsen)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148791,"Si Kujur di ladang kapas","Kembanglah bunga perautan","Kalau mujur bonda melepas","Seperti ayam pulang ke pautan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148792,"Si kujur diladang kapas","Kembanglah bunga peraturan","Kalau mujur bunda melepas","Bak ayam pulang ke pautan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148793,"Si kunyir belayar mudik","Masuk perlahan di Sungai Pandan","Walau cita cantik dan molek","Inda mufakat menjadi muram", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148794,"Si Kupang berbaju besi","Akan terumpak si buku buluh","Dikapung aku tiada mati","Aku memakai sangga bunuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148795,"Si Kupang berbaju besi","Akan terumpak si buku buluh","Dikepung aku tiada mati","Aku memakai si sangga bunuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148796,"Si Lalang berpuntil bata","Gelang geruncung beri bertali","Alang-alang bermain mata","Baik dilangsung sama sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148797,"Si Lanyap namanya kumbang","Terbang bersama anak kunang'kunang","Hilang lenyap jangan ditumang","Itu namanya anak laki-laki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148798,"Si Lanyap namonyo kumbang","Tabang jo anak api-api","Hilang lanyap jangan ditumang","Namonyo anak laki-laki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148799,"Si miskin mendapat derma","Dari uang hasil usaha","Jangan tanyakan agama","Orang tak bisa berpura-pura", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148800,"Si Muncak mati tarambu","Ka ladang mambao ladiang","Adaik jo syarak di Minangkabau","Ibaraik aua jo tabiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148801,"Si patung menyisir rimba","Mati di palu rotan bulat","Elok pasungkan hati nan gila","Antara iman dan mudzarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148802,"Si patung menyusur rimba","Mati dipalu rotan bulat","Elok pasungkan hati nan gila","Antara iman belum mudarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148803,"Si pemikat mengukamit","Beramai-ramai ke Tanah Jawa","Andang iktikad selagi damit","Lama di lama sampai jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148804,"Si pemikat mengulamit","Beramai-ramai sampai ke Jawa","Andang atikat selagi damit","Lama-dilama sampai jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148805,"Si raja menjadi saksi","Pisau raut belah pemanggang","Kera di hutan pandai meraksi","Ikan di laut naik berganggang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148806,"Si Rajaudang menjadi saksi","Pisau raut bilah pemanggang","Kera di hutan pandai meraksi","Ikan di laut naik berganggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148807,"Si rajawali menyambar merpati","Habis luruh sekalian bulu","Bagaimana hendak mencari ganti","Sudah berkarar dari dahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148808,"Si Reman anak rang Canduang","Anak-samang Bandaro Kunieng","Sadang di-sada bundo-kanduang","Kiro-nyo lauik ba-kuliling (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148809,"Si Rodi anak rang Pauah","Dapek hokum-an limo ari","Usah ba-dagang jauah-jauah","Ilang ka-mano mandeh cari (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148810,"Si senduduk di bawah rumah","Uratnya melintas sendi","Tinggallah ibu yang punya rumah","Kami berjalan segera pergi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148811,"Si Sidik rang Koto Gadang","Pandai bakoa bacokian","Adiak dimabuak bungo kambang","Kami dimabuak paratian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148812,"Si Sidik rang Koto Gadang","Pandai bermain koa bercekian","Adik dimabuk bunga kembang","Kami dimabuk perhatian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148813,"Si Talun rang Ampek Angkek","Basikek dalam kalumun","Lah pueh kami bapikek","Balam nan tidak amuah turun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148814,"Si Talun rang Ampek Angkek","Menyisir sambil berkelumun","Lah puas kami memikat","Balam tidak mau turun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148815,"Si Tangka Lapa nan di ujuang","Bakeh nan bansaik salang tenggang","Panangka lapa dalam kampuang","Untuak ka serap di musim gantuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148816,"Si tobal daun sitobal","Talotak di ate lutuik","Sangat bobal hamba Allah","Kaji sapatah indak tatuntuik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148817,"Si tobal daun sitobal","Terletak di atas lutut","Sangat bebal hamba Allah","Kaji sepatah tidak dituntut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148818,"Si Tongkat Ali petala bumi","Ubat khasiat akar kayu","Nasihat petunjuk berikan kami","Agar tak sesat umat Melayu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148819,"Si Tuah melarikan dulang","Papan dilarik Si Naga Wangsa","Tiada sedar jiwa pun hilang","Mayat diambil Raja Melaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148820,"Si Tuah menangkap kupu-kupu","Minyak ketiau atas lenja","Daripada sangat tuahnya Upu","Di tanah Riau menjadi raja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148821,"Si tujuh di Batang Kapas","Kembanglah bunga perawitan","Kalau boleh bunda melepas","Bagai ayam pulang ke pautan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148822,"Si Ulando di Batusangka","Bajambek batali tuduang","Umpamo buruang dalam sangka","Mato lapeh badan takuruang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148823,"Si Belanda di Batusangka","Berjambang bertali tudung","Umpama burung dalam sangkar","Mata lepas badan terkurung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148824,"Si Upiak di Bangkaulu","Pandai mangarang bungo padi","Manembak tidak bapiluru","Putuih jantuang pangarang ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148825,"Si Upik di Bangkaulu","Pandai mengarang bunga padi","Menembak tidak berpeluru","Putus jantung pengarang hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148826,"Si Utuih anak rang Padang","Badeta baliak barendo","Di lauik bungo nan kambang","Di darek kumbang nan gilo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148827,"Si Utuih anak orang Padang","Berdestar balik berenda","Di laut bunga yang kembang","Di darat kumbang menggila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148828,"Si Utuih pandai ma-nari","Ma-nari di-ateh dulang","Kaia putuih ikan lah lari","Tariek juaran bao pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148829,"Si Yakuik namo nakhodonyo","Ulando pulang ka Batawi","Tidak tasangkuik di aragonyo","Bara bajua sayo bali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148830,"Si Yakuik nama nakhodanya","Belanda pulang ke Betawi","Tidak tersangkut pads harganya","Berapa dual saya beli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148831,"Sialang Pulau Sialang","Tiga dengan Pulau Gelugor","Kasih tuan bukan kepalang","Jatuh di hati lempa gugur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148832,"Sialang Pulau Sialang","Ketiga dengan Pulau Gelugur","Kasih tuan bukan kepalang","Tiba di hati jantung nak gugur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148833,"Siamang berburu puyuh","Hingga di pagar pesamaian","Kalau teringat akan orang jauh","Ambil api panggang kemenyan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148834,"Siamang di ateh tunggua","Manitih titian pantai","Tagamang tagolek tidua","Ampehkan tangan ka lantai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148835,"Sian burong Sian","Sian men-gingap klopak jantong","Tuan tidak balas kesian","Tempat di mana saya bergantong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148836,"Sian Laru le tu Tambangan","Batang Gadis melduk-elduk bahat muse lubuk na","Untung ma baya adong Lubuk Rarangan","Garing dohor mera leng sonang angoluanna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148837,"Siang berakit malam bersampan","Cari jelawat di Kuala Mukah","Belanja sedikit banyak menyimpan","Wajib haji kita ke Makkah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148838,"Siang dan malam mereka menjaga","Para pesakit dan doktor muda","Agar rawatan tidak tergendala","Syabas dan Selamat Hari Jururawat Sedunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148839,"Siang hari pasang pelita","Sudah terang lagi bersuluh","Hendak tidur-tidurlah mata","Usah dikenang si orang jauh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148840,"Siang malam berganti hari","Musim kemarau dengan hujan","Kebaikan sejati dari hati","Semua bersih ketika dilahirkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148841,"Siang malam di mata-mata","Hati saya tiada berhenti","Saya ingat kepada Norma","Tidur sekejap bermasuk mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148842,"Siang malam memotong kayu","Naik ke rumah badan pun letih","Abang demam menanggung rindu","Adik gelisah menahan sedih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (148843,"Siang malam menjunjung buih","Ombak tak jemu mengusap pantai","Siang malam jiwa merintih","Hati rindu minta dibelai", 4, 6);
