INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149563,"Sudah bertudung terendak Bentan","Pagar siapa saya sesarkan","Sudah untung permintaan badan","Dengan siapa saya kesalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149564,"Sudah bertunang jangan dipinang","Anak dara duduk sebilik","Kerja susah menjadi senang","Hasil muafakat adik-beradik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149565,"Sudah bertunas si pokok turi","Baru dialih ke tempat redup","Hujan panas permainan hari","Suka duka asam garam hidup", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149566,"Sudah bongkok jangan menyeranggoh","Boleh susup dari bawah","Orang tua bongkok mengelisah gadoh","Kena serkup di bawah kawah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149567,"Sudah bongkok jangan menyerangguh","Boleh susup dari bawah","Orang tua bongkok menggelisah gaduh","Kena serkup di bawah kawah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149568,"Sudah buaka tungganglah ongkow","Bak uek si kayu ao","Kupado mamak soto kawan-kawan bubaiklah ongkaw","Kupado kuabat sodao demikian pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149569,"Sudah dahulu ada sekarang","Tata adat dan upacara","Tentang kekurangan seseorang","Rahasia empuk untuk bicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149570,"Sudah dapat batang gaharu","Batang cendana diketepikan","Sudah dapat teman yang baru","Kawan lama jangan dilupakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149571,"Sudah datang kapal serati","Mari berlaboh pangkalan panjang","Sudah datang si buah hati","Perutku lapar menjadi kenyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149572,"Sudah datang kapal serati","Singgah ke pasar membeli barang","Sudah datang si buah hati","Hati susah menjadi senang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149573,"Sudah di lantai dilantakkan","Emas berlantak berat sekati","Kepada siapa saya katakan","Cinta mendendam di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149574,"Sudah di rasa bharu kan tau","Kopi chap 'KUDA' betol baik  rasanya","Saja di karang panton mengasi tau","Chobak di bli bharu tau rasa dan bahu-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149575,"Sudah di sauk jangan di sudok","Jatuh balik susah di cedok","Petik benang arang cengkok-cengkok","Kalau di tarah tengah tergelohok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149576,"Sudah dicuci lalu diperah","Jemur baju di tepi huma","Lenyap benci hilang marah","Segar kembali cinta lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149577,"Sudah diikat bunga seuntai","Jangan dipanjat pagar di taman","Jering lekat busuk di tangkai","Duit sejat dalam genggaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149578,"Sudah di-kana petok tedong","Lagi jerit lagi menangis","Ibarat sabagi buah kedondong","Dulu kelat kemdian manis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149579,"Sudah dimakan kulit dibuang","Jatuh terkena kain jemuran","Cuma alasan kepunyaan orang","Harta dunia jadi ukuran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149580,"Sudah dimakan kunyit dan temu","Ubat sakit si batu karang","Keluar rumah menuntut ilmu","Darjatnya bak ke medan perang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149581,"Sudah dimasak segala juadah","Anak bangsawan diundang datang","Jalan sesak saya redah","Kerana tuan saya datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149582,"Sudah dipanggang si ikan pari","Dimakan dengan sambal cuka","Siapa pandai membawa diri","Ke mana pun pergi orang suka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149583,"Sudah dipilih lalu disukat","Padi bersih dari ladang","Tamu dijemput jauh dan dekat","Jiran sebelah tidak diundang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149584,"Sudah dipuja sekalian dewata","Dah berlari ke dalam taman","Hendak dijadikan kemuncak mahkota","Tiku Benua dengan Pariaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149585,"Sudah disangai lalu digiling","Harum kerisik si rending Minang","Buruk perangai jumpa dijeling","Hati terusik dendam dikenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149586,"Sudah di-tarek lalu perti","Naik kuda pasang pelana","Buat baik juga terpuji","Biar di-bawa di-mana-mana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149587,"Sudah ditebang ditabur benih","Durian sepokok di tepi telaga","Kasih abang bak air jernih","Minum seteguk hilang dahaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149588,"Sudah di-tebang mari di-ukir","Di ukir dengan pisau wali","Abang sa orang tiada-lah mungkir","Tinggal lah tuan abang kombali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149589,"Sudah ditebang marl diukir","Diukir dengan pisau wali","Abang seorang tidaklah mungkir","Tinggallah tuan abang kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149590,"Sudah ditebang menanti condong","Sayang penujuh hilir ke kota","Bila dipandang matahari condong","Rasa nak jujuh si air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149591,"Sudah ditolak penunggu pintu","Kedua pihak seorang satu","Tembaknya bukan meriam berhantu","Pelurunya bukan besi dan batu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149592,"Sudah ditulis tidak berbunga","Dari kuala lalu ke pekan","Sudah dapat tidak berguna","Berasa malu tuan lepaskan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149593,"Sudah ditutuh lagi ditebang","Buat tempat ampaian kain","Tempat jauh lagi dikenang","Inikan pula tempat bermain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149594,"Sudah gaharu cendana pula","kura-kura dalam perahu","tuan lah tahu bertanya pula","berpura-pura belum tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149595,"Sudah gaharu cendana pula","Tetak temu dalam puan","Sudah tahu bertanya pula","Hendak bercumbu gerangan tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149596,"Sudah gembur baharu menukai","Benih dibawa dalam bungkus","Menjalar melata pohon temikai","Sayang buah dimakan tikus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149597,"Sudah gembur baharu menukai","Benih dibawa dalam bungkus","Menyelar melaya pohon tembikai","Sayang buah dimakan tikus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149598,"Sudah gembur baru menuai","Benih di bawa dalam bungkus","Menyelar melata pohon tembikai","Sayang buah di makan tikus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149599,"Sudah hilang panas di padang","Tinggallah saya panas di rimba","Sudahlah hilang hati nan sayang","Tinggallah saya hati nan hiba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149600,"Sudah hilang panas di padang","Tinggallah sahaja panas di rimba","Sudah hilang hati nan sayang","Tinggallah sahaja hati nan hiba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149601,"Sudah jeling jangan di jeluat","Hati marah sabar dulu","Mata juling serong melihat","Jauh bertentang tidak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149602,"Sudah jeling jangan dijeluat","Hati marah sabar dahulu","Mata juling serong melihat","Jauh bertentang tidak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149603,"Sudah jelujur biku lipatan","Di atas peti rumah di desa","Semua penganjur pilih Perikatan","Sudah terbukti menabur jasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149604,"Sudah ku bilang jalannya licin","Kenapa adik naik sepeda","Sudah kubilang diriku miskin","Kenapa adik cinta jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149605,"Sudah ku coba ke Payakumbuh","Padi di sawah lagi bunting","Ada ku coba menghilang rusuh","Hati tak mau diajak berunding", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149606,"Sudah ku coba pergi ke Deli","Akhirnya balik ke kota Medan","Sudah ku coba berjudi kasih","Akhirnya balik kepada tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149607,"Sudah ku coba pergi ke Deli","Tidak Deli sebaik Medan","Sudah ku coba berjudi kasih","Tidak orang sebaik tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149608,"Sudah ku tau priah pait","Maka tidak di Petek buah-nya","Sudah ku tau akar-nya terkait","Maka sengaja di gegar pokok-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149609,"Sudah kubilang kalannya licin","Kenapa adik naik sepeda","Sudah ku bilang diriku miskin","Kenaoa adik cinta jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149610,"Sudah kucuba si kuda pasu","Sewaktu temasya orang menari","Kera di rimba ku beri susu","Seperti terasa anak sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149611,"Sudah kuning buah rambutan","Ambil upah mencari galah","Sakit pening sudah suratan","Bukan sumpah bukan tulah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149612,"Sudah kutahu peria pahit","Tapi kumasak bersama gula","Sudah kutahu bercinta sakit","Hati terpaut sejak mula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149613,"Sudah lama berikat pinggang","Ikat pinggang rambu berjurai","Sudah lama adat dibilang","Adat dibilang lalu dipakai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149614,"Sudah lama berpenyakit semput","Jenis ubat dapat dibilang","Umpama embun di hujung rumput","Datang panas teruslah hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149615,"Sudah lama buluh dilentur","Baru kini dibuang tali","Alim ulama seperti doktor","Mengubat aneka penyakit hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149616,"Sudah lama kami ke tasik","Tali perahu terap belaka","Sudah lama kami merisik","Baru kini bertemu muka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149617,"Sudah lama kami menebang","Tidak mendengar tumbang kayu","Sudah lama kami memandang","Tidak menengok abang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149618,"Sudah lama langsatnya condong","Baru kini batangnya rebah","Sudah lama niat terkandung","Baru kini dimakbul Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149619,"Sudah lama langsatnya condong","Baru kini dirampaian","Sudah lama niat dikandung","Baru kini kesampaian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149620,"Sudah lama langsatnya condong","Barulah kini batangnya rebah","Sudah lama niat dikandung","Baru sekarang diizinkan Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149621,"Sudah lama langsatnya condong","Barulah kini batangnya rebah","Sudah lama niat dikandung","Barulah kini disimpaikan Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149622,"Sudah lama langsatnya condong","Dahannya rebah ke ampaian","Sudah lama niat dikandung","Baru sekarang disampaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149623,"Sudah lama mengikat tudung","buah kuini mengampai dahan","sudah lama niat dikandung","barulah kini disampaikan Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149624,"Sudah lama merendam selasih","Barulah kini mau mengembang","Sudah lama ku pendam kasih","Barulah kini bertemu pandang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149625,"Sudah lama tidak ke Kudat","Air naik di Sunagi Tuaran","Cara lama menurut adat","Mana yang baik tinggalkan jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149626,"Sudah lama tidak ke Kudat","Sikit berbelok dan ada simpangan","Cara lama menurut adat","Adat yang elok di tinggalkan jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149627,"Sudah lama tidak ke ladang","Habis padi dililit kangkung","Sudah lama tidak kupandang","Hancur hati pengarang jantung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149628,"Sudah lama tidak ke ladang","Tinggi rumput dari padi","Sudah lama tidak kupandang","Sampai pedih dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149629,"Sudah lama tidak ke ladang","Tinggilah rumput dari padi","Sudah lama tidak kupandang","Rasa rindu menjadi-jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149630,"Sudah lenggang-kah blom","Slendang di atas bahu","Sudah chiom-kah blom","Itu-lah obat hati rinu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149631,"Sudah letih menjual padi","Jangan pula dikata-kata","Sudahlah sedih ditinggal pergi","Jangan pula hamba dinista", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149632,"Sudah licik baharu di tenyeh","Pisang di bakar api menyala","Kuntum tersenyum sudah ronyeh","Tampal tak boleh balik semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149633,"Sudah luca tercampur leceh","Tiada menjadi aib dan sopan","Susu lanjut kepala terpersel","Untang-anting di tengah jalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149634,"Sudah lurus batang dititi","Ambil sebatang dibelah dua","Sudah kurus abang menanti","Adik tak datang datang juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149635,"Sudah lurus jalan ke hulu","Senanglah kaki jalan melenggang","Sudah kurus badan menunggu","Abang dinanti tak kunjung datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149636,"Sudah malam gurda terbang","Kenaikan sultan raja yang sakti","Bercinta dendam rasanya abang","Molek sahaja di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149637,"Sudah masak buah cempedak","Ditusuk-tusuk dengan seligi","Sudah lama adik teregak","Jangan abang pergi lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149638,"Sudah masak buah cempedak","Masak sebuah di dalam peti","Sudah lama adik teregak","Kini bersua senanglah hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149639,"Sudah masak buah kepayang","Buah melinjo di buat emping","Mari adik marilah sayang","Sudah lama tidak berdamping", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149640,"Sudah masak buah kerutung","Masak sebiji dimakan kera","Sudah ku tanya ke tukang tenung","Dindalah yang membuat gila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149641,"Sudah masak buah mangga","Bersama salat di peti mercun","Fitnah perosak rumah tangga","Umpama coklat berinti racun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149642,"Sudah masak buah mangga","Bersama salat di peti mercun","Fitnah perosak rumah tangga","Umpama coklat berisi racun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149643,"Sudah masak buah mangga","Bersama salat di peti racun","Fitnah perosak rumah tangga","Umpama coklat berisi racun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149644,"Sudah masak langsat dilorong","Masak di tengah tiga tangkai","Jangan pembongkak jangan pembohong","Mana 'nak cari emping bertangkai?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149645,"Sudah masak nasik","Pi ke Kampong Bohoq","Saya jumpa pak cik","Leteh tidoq lunjoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149646,"Sudah melangkah jangang dianjur","Pohon nyiur buah kerambil","Sungguh hamba berkata jujur","Kalau ada silakan ambil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149647,"Sudah memerun baharu menukal","Hendak tanam padi huma","Saya umpama benang setukal","Putus satu rosak semua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149648,"Sudah memerun baharu menukal","Hendak menanam padi huma","Saya umpama benang setukal","Putus satu rosak semua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149649,"Sudah memerun baru menugal","Hendak menanam padi huma","Saya umpama benang bertukal","Putus satu rosak semua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (149650,"Sudah mendidih bubur disenduk","Sebelum dijamu rasa sesudu","Sakit pedih bak sembilu ditusuk","Sakit lagi ditusuk rindu", 4, 6);
