INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150081,"Sunnah Nabi ikut juga","Salah satu sumber hukum Islam","Wujudlah kasih sayang sesama keluarga","Nampak yang salah jangan diam", 4, 6);
