INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150083,"Suntingan anak raja udara","Kain kocak berlerang-lerang","Janganlah tuan membuat cura","Yang lain masakan kurang", 4, 6);
