INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150089,"Sunyi sepi orang dirumah","Kerana mata sudah mengantuk","Apa sebab mandi tak basah","Kerana cinta membawa mabuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150090,"Suok dunia baberang-berang","Kidanyo marungi tak balabo","Rumah dunia ria jo riang","Pagawainyo pitanah samato-mato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150091,"Sup sayur ikan kerisi","Daun sudri ditabur sarat","Diam ubi diam berisi","Diam besi terus berkarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150092,"Supaya beban mudah diangkat","Eloklah dibawa beramai-ramai","Supaya badan kita selamat","Elok tak usah memandai-memandai", 4, 6);
