INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150420,"Tak guna kacang dikebat","Biar berserak supaya tumbuh","Tak guna dagang dihambat","Biar ditolak supaya jatuh", 4, 6);
