INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150423,"Tak jelek hanya berbugil","Apa arti berpakaian","'Berniat' saja tidak jahil","Tak usah sampai laksanakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150424,"Tak katup pintu","Ayam makan padi","Tak kata begitu","Nak kata macam mana lagi", 4, 6);
