INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150426,"Tak kuniang karano kunyik","Tak putiah karano kapua","Tak gadih dek kain basalang","Tak kayo dek ameh pambao", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150427,"Tak lamak gulai rang Sumpu","Tak kalah gulai ayam sabuangan","Tak dapek di den di pintu","Den rateh tulang bubuangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150428,"Tak lemak gulai rang Sumpu","Tak kalah gulai ayam sabungan","Tak dapat menghadang dari pintu","Kuretas tulang bumbungan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150429,"Tak lekang dek panas","Tak luntur dek hujan","Bak putik baru bertunas","Irama asli menjadi pujaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150430,"Tak mahu gulai nangka","Nak ndak gulai cempedak","Tak mahu kakak janda","Nak ndak anak dara jugak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150431,"Tak nak gulai cempedak","Nak ndak gulai durian","Tak nak kanak-kanak","Nak ndak anak dara belasan", 4, 6);
