INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150436,"Tak pernah kita merainya secara rasmi","Sungguhpun Alam Sekitar tersemat di hati","Mungkinkah kerana Dato Seri Azmi","Majlis disambut dengan berseri seri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150437,"Tak pernah memakan rending","Setalam mana nan dijimati","Tak pernah orang kaya bertandang","Dendam mana nan dituruti", 4, 6);
