INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150439,"Tak talang salujua nangko","Tandikek  jalan ka parak","Tak malang samujua nangko","Garaman tumbuah di katiak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150440,"Tak talang selujur ini","Tandikek jalan ke parak","Tak malang semujur ini","Geraham tumbuh di ketiak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150441,"Tak usah jawab pertanyaan","Bertemu tanpa janjian","Tak ada selalu kekecewaan","Juga keberhasilan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150442,"Takabek pimpiang di Balimbiang","Itu sangakaran balam tabu","Takilek cincin di kalingkiang","Itulah ubek ati rindu", 4, 6);
