INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (150444,"Takaduhung lompat keratang","Ambil papan pembelah kundur","Takaduhung ke mari datang","Haram setapak mahu berundur", 4, 6);
