INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151332,"Tebang tebu panjang pendekan","Buat pendayung ke selat Malaka","Ambil ilmu sungguh-sungguhan","Untuk lampu masuk ke surga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151333,"Tebang tebu sampai ke ujung","Sampai ujuangnyo ka Melaka","Tuntuik alemu ganti payuang","Payuang pandendeng api noroko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151334,"Tebang tebu Sungai Rengit","Tujuh batang sembilan buku","Cuba hitung bintang di langit","Tujuh laksa sembilan ribu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151335,"Tebang tebu tasindayung","Tasindayung ke Melaka","Tuntut ilmu kan ganti payung","Payung pendinding api neraka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151336,"Tebang tebu tinggi-tinggi","Sama tinggi dengan durian","Tuntut ilmu masa kini","Jangan menyesal kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151337,"Tebang tebu tinggi-tinggi","Tebang rumbia terpangkal-pangkal","Tuntut ilmu masa kini","Hidup di dunia tidak kekal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151338,"Tebang tebuk di Kuala Salah","Hujan di gunung sampai ke kampung","Hidup di dunia serba salah","Tak ada tempat mengadu untung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151339,"Tebang temak dengan kapak","Mari dipotong panjang seela","Ditelan emak diluah bapak","Hendak dikulum sampai bila?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151340,"Tebang tengar berkalok paku","Buah berangan di padang saujana","Dengarlah sembah jika berlaku","Dengan tuanku gundah gulana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151341,"Tebang tepi tiga segi","Jatoh ka ayer berserak rata","Abang pergi tiada kembali","Jatoh mengalir si ayer mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151342,"Tebanglah kayu sebatang dua","Untuk membuat perahu kolek","Abang merindu sepanjang masa","Mabuk melihat tubuh yang molek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151343,"Tebanglah lemba akan titian","Anak tempua makan pagi","Sedang adinda lagi sekian","Jika kakanda apatah lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151344,"Tebang-tebang pokok","Jumpa duit emaih","Sembang-sembang tengok","Besok boleh tebaih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151345,"Tebarkan jala di laut lepas","Menjala ikan di tengah hari","Relakan saja kekasih lepas","Kalau jodoh datang sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151346,"Tebas lalang diganti bayam","Tanam bayam bercambah dipasu","Gagah helang menyambar ayam","Gagah insan melawan nafsu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151347,"Tebas menebas orang dirimba","Untuk membuat ladang pematang","Tubuhku lemas hilang selera","Menengok tabiat abang memandang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151348,"Tebas menerung atas permatang","Tempat bertelur si burung puyuh","Selesai bertarung ayam pun menang","Janji ditabur payah nak tumbuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151349,"Tebas-tebas rumput di dusun","Durian gugur memang ditunggu","Lepas bebas langkah tersusun","Ramah ditegur mesranya cikgu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151350,"Tebat berisi ikan tenggiri","Anak kera makan rambutan","Sungguh pandai tuan memuji","Membuat saya lupa daratan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151351,"Tebat ikan di Kertapati","Cina membakar daun setanggi","Adik umpama burung merpati","Hendak ditangkap terbangnya tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151352,"Tebedagang mete bati","Dendeq jari penghianat","Rajin sembahyang rajin ngaji","Jari bekal leq akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151353,"Tebegawe gorok sampi","Sampi te gorok gentik bembek","Maraq idaq ku siq ngimpi","Bedait bareng sahabat laek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151354,"Teberau datang ke beting","Tempua datang ke Judah","Terderau air talkin","Petua kita jangan diubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151355,"Teberau dilanggar patin","Dirompak patin tebing seberang","Berderau air talkin","Orang yang banyak berbalik pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151356,"Tebikar bawa kepaya","Tua bertating tating","Orang sukar baharu kaya","Mulut terpanting panting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151357,"Tebing Tinggi kampung ternama","Nampak dari Kuala Segamat","Bila teringat budi yang lama","Berubah hati berdebar semangat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151358,"Tebing Tinggi kampung ternama","Nampak dari Kuala Segamat","Bila teringat budi yang lama","Berubah hati berdebar semangat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151359,"Tebing tinggi pangkalan ikan","Anak Cina menjual telur","Adik di sini rindu tak makan","Abang di sana rindu tak tidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151360,"Tebu betung dalam paya","Ambil sebatang belah empat","Sudah tak untung badan saya","Ikan di pegang balik melompat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151361,"Tebu betung dalam paya","Ambil sebatang belah empat","Sudah tak untung nasib saya","Ikan dipegang balik melompat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151362,"Tebu bukannya bangkut","Rama-rama terbang ke kuala","Bukan kami kaki penakut","Berani kami berkorban nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151363,"Tebu bukannya bangkut","Rama-rama terbang ke kuala","Bukan kami kaki penakut","Berani kami berkorban nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151364,"Tebu di makan buku di taruh","Makanan anak muda bangsawan","Bukit berlaga gunung bertaruh","Ramai bertepuk mambang di awan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151365,"Tebu dijual orang seberang","Dibeli orang Indragiri","Sampai hati kiranya abang","Adik ditinggal seorang diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151366,"Tebu hitam dikerat-kerat","Dikerat sama panjang sejengkal","Dunia ini kebun akhirat","Amal usaha mengumpul bekal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151367,"Tebu hitam panjang sedepa","Buat ubat demam cika","Tubuh hitam cantik juga","Hembus pelita sama belaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151368,"Tebu kance paoq masak","Tebait isiq dengan","Pacu-pacu jari kanak","Adeq side taoq ntan dengan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151369,"Tebu lanjut panjang lampai","Permain-an anak raja di Juda","Ajat di hati kalu tak sampai","Rosak badan dendam tak suda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151370,"Tebu liat si tebu lanjung","Dikilang dipanas matahari","Jatuh berderak tangga anjung","Kerana abang pulang dinihari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151371,"Tebu liat tebu sejunjung","Tebu kapur tengah laman","Kami lihat dalam nujum","Sirih kapur dalam sapu tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151372,"Tebu sejengkal buluh sedepa","Ayam sepaha nasi dibekal","Menjana akal memerah minda","Habis usaha baru tawakal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151373,"Tebu seruas di dalm ayer","Sayang lengkudi batang selaseh","Hendak pulang sayang sayangan","Baru nampak kaseh sayang nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151374,"Tebu tua dahulu tebang","Di pangkalnya sandarkan bedil","Meski islam tidak melarang","Apakah mampu berbuat adil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151375,"Tebu yang tua dikerat-kerat","Pucuknya ada dipangkal pinang","Dari dunia sampai akhirat","Seumur hidup tetap dikenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151376,"Tebuk awan berkalok-kaluk","Tepi di jahit dengan renda","Pokok kayu besar jangan dipeluk","Kalau gagah meluncut dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151377,"Teduh bernaung pohon pulasan","Tepian desa berbuah berangan","Tanda bersambung adat warisan","Budaya bangsa dilupa jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151378,"Teduh lautan tenang di kuala","Riuh di jeti nelayan pulang","Busuk ikan mula di kepala","Busuk hati ikhlasnya hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151379,"Teduh mata menatap zamrud","Batu hijau jadi pilihan","Bersih hati tunduk sujud","Menyerah diri kepada Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151380,"Teduh pagi menanam kentang","Tanam berselang pokok kedondong","Tujjuk pelangi melanggar pantang","Pelangi hilang jari tak kudung!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151381,"Teduh rending pohon cemara","Batang ditoreh tidak bergetah","Lama bertandang si duka lara","Gelap hati mencari arah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151382,"Teduh samudera berkilau hening","Di senja kala nyaman berseri","Sungguh wira hijau kuning","Juara bola haruman negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151383,"Teduh samudera berkilau kuning","Di senja kala nyaman berseri","Sungguh wira Hijau Kuning","Juara bola haruman negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151384,"Teduh teluk pantai pun bersih","Air pasang kapal berlabuh","Puas memujung menabur kasih","Hati gersang cinta tak tumbuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151385,"Tegak adat hidup senonoh","Pemimpin adil rakyat sejahtera","Rusak adat hidup bergaduh","Pemimpin degil rakyat durhaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151386,"Tegak adat hidup sentosa","Rakyat tenang pemimpin terpuji","Rusak adat bertambah dosa","Laknat datang sepanjang hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151387,"Tegak adat hidup terpandang","Pemimpin senang rakyat selamat","Rusak adat hidup menyimpang","Pemimpin kenyang rakyat melarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151388,"Tegak adat kokohlah bangsa","Hidup rukun tenggang menenggang","Rusak adat tumbuh sengketa","Sengketa tumbuh nasib pun malang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151389,"Tegak adat rakyat bahagia","Pemimpin terpuji muka belakang","Rusak adat rakyat teraniaya","Pemimpin keji balapun datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151390,"Tegak adat rakyat pun damai","Pemimpin tegak bersanding bahu","Rusak adat rakyat berkecai","Pemimpin bengak rakyat berseteru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151391,"Tegak adat rakyat pun taat","Pemimpin elok berkata jujur","Rusak adat rakyat khianat","Pemimpin teruk lidah terjulur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151392,"Tegak adat syarak berdiri","Hidup selamat dunia akhirat","Rusak adat rusaklah negeri","Hidup sesat sengsara umat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151393,"Tegak adat tegaklah tuah","Tuah menjadi payung negeri","Rusak adat tercampak marwah","Marwah tercampak lancunglah diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151394,"Tegak adat teguhlah umat","Umat teguh hidup pun damai","Rusak adat tumbuhlah laknat","Laknat tumbuh hidup meragai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151395,"Tegak berdiri pohon mertajam","Pohon ditebang dibuat papan","Berbadan panjang bermata tajam","Hendak pakai dibuang tidak pakai disimpan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151396,"Tegak berdiri si pohon palma","Daun menguning di batang bawah","Berbekallah dengan Rukun yang lima","Diri tidak dipandang rendah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151397,"Tegak berdiri si pohon sena","Daunnya jatuh ke dalam perigi","Tidak berharta insan dihina","Cinta dijaja tak laku lagi!", 4, 6);
