INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151409,"Tegak kukuh di atas kaki","Kaki buat penahan badan","Senag bertemu si jantung hati","Makanya gelak tidak tertahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151410,"Tegak mahligai cantik terbilang","Dipandang tak cukup sekali","Kasihku bagai lalang di padang","Ditebas dipijak tumbuh kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151411,"Tegak pagar kayunya lurus","Pagar berduri keliling laman","Pancang bergeger dirempuh arus","Berdebar hati menanti hukuman", 4, 6);
