INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151420,"Tegak selasih berurat tunggang","Bijinya kembang dibuat obat","Banyaklah kasih disebut orang","Berkasih sayang membawa manfaat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151421,"Tegak tiang kerana pasak","Keras lilin dibakar cair","Duduk berdiang nasi pun masak","Sambil menyelam minum air!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151422,"Tegaknya pokok tepi telaga","Nampak tegap lurus selari","Menghisap rokok mencari syurga","Di dalam asap nampak bidadari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151423,"Tegaknya rimbun si pohon kenanga","Di Bukit Batu tersergam indah","Bakar embun ambil abunya","Baru hatiku berpaling tadah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151424,"Tegak-tegak cocokkan pancang","Pasang bendera bunyikan tabuh","Agak-agak mengatai orang","Supaya cedera jangan tumbuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151425,"Tegak-tegak cucukkan pancang","Pasang bendera bunyikan tabuh","Agak-agak mengata orang","Supaya cedera jangan tumbuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151426,"Tegap kasau si kayu jati","Kisi-kisi sawa menggelapai","Bigar sasau gelinya hati","Kering gusi penat mengilai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151427,"Tegap lantai si kayu cengal","Tiangnya utuh lama dipakai","Kusut masai kepal pun Bengal","Saham jatuh rumah tergadai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151428,"Tegap pagar kayu dicacak","Batang ara dibuat perahu","Cakap berdegar lagak si bongkak","Semua perkara dia yang tahu!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151429,"Tegap palas akarnya teguh","Tambak batang nan bercambah","Sikap malas kerja bertangguh","Bak hutang kian bertambah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151430,"Tegap paruh terbayang helang","Turun hinggap di pokok mengkudu","Adik jauh tidak terjelang","Gambar ditatap mengubat rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151431,"Tegap rumah atapnya ijuk","Tiangnya pula kayu jati","Bersembunyi di sebalik telunjuk","Ada saksi kurang bukti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151432,"Tegap rumah tiangnya nyatuh","Jangan ditutuh duku sebatang","Jualan murah harga runtuh","Bahu runtuh memikul butang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151433,"Tegap tiang takuk tak  lut","Kepal tangga si ular berang","Lentik kening si pisau raut","Hidung jejaka selabu bawang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151434,"Tegar karang tiada tergoyang","Tak lekang juga di terik matahari","Bahwa cinta dan kasih sayang","Bukan mengambil tapi saling memberi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151435,"Tegarnya karang tiada tergoyah","Meski badai banyak melanda","Beliau tak pernah melihat ayah","Masih kecil pun di tinggal bunda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151436,"Tegitaq lolon jeruk langsung tetaeq","Seuahte entun masih aman ndareq dengan","Aji sembako sere nani terus taeq","Akibatna dengan miskin jari korban", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151437,"Tego tego tali perambot","Mau di buat ikat kan panching","Kalu tak peno pasang di laot","Takot ter-galang kapal di beting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151438,"Teguhkan iman hambamu ini","Dalam menempuh kilau dunia","Sungguh banyak dosa dihati","Meluluh kan iman di dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151439,"Teguhkan iman hambamu ini","Dalam menempuh kilau dunia","Sungguh banyak dosa dihati","Meluluh kan iman di dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151440,"Teguh-teguh bendera raja","Talinya mari dari subarang","Teguh-teguh setia saja","Dari dahulu sampai sekarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151441,"Tegun Lembu bukit berbatu","Banyak terbang si rama-rama","Banyak ilmu tak guna satu","Kalau jahil dalam agama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151442,"Teka teki dalam bahasa","Jawabannya boleh dicari","Orang menipu terbiasa","Menipu juga diri sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151443,"Tekaduhung lompat keratang","Ambil kapak pembelah kundur","Tekaduhung ke mari datang","Haram setapak tidak kuundur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151444,"Tekan saydak bunyi rebana","Bunyi gong ruma Chek Ali","Tidak sebab tidak kerna","Saya tergantong tidak bertali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151445,"Tekan saydak bunyi rebana","Bunyi gong rumah chek ali","Tidak sebab tidak kerna","Saya tergantong tidak bertali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151446,"Tekan sehdak pukol rebana","Bunyi gong di rumah Chek Ali","Tidak sebab tidak kerna","Sahya tergantong tidak bertali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151447,"Tekan seydak bunyi rebana","Bunyi gong rumah Chek Ali","Tidak sebab tidak kerna","Sabya tergantong tidak bertali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151448,"Tekan seydak bunyi rebana","Bunyi gong rumah Chek Ali","Tidak sebab tidak kerna","Sabya tergantong tidak bertali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151449,"Tekat baju jualan kota","Tetap teringat setiap masa","Daulat Tuanku Payung Mahkota","Sembahan rakyat bahagia sentiasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151450,"Teknologi maklumat kebanggaan kita","Bersembang siber bertalipon selular","Aduhai tibalah masanya","Acara berbalas pantun akan bermula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151451,"Tekukor mati tergantong","Mati tergantong di pohon nangka","Nak gugur hati dan jantong","Bilah ter-ingat yang suda suda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151452,"Tekukor terbang tinggi","Mari di sambar burong lang","Sa-umor idop bersobat lagi","Simpan di gendong jangan ilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151453,"Tekukur balam Mak Judah","Dikandangkan dua-dua","Putuskan kata hingga sudah","Jangan badan begini jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151454,"Tekukur burung sembangan","Singgah memakan buah pedada","Kekanda tidur berbantal lengan","Urai rambut penghalang dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151455,"Tekukur di pauh tinggi","Mari makan bunga selasih","Jika diukur sama tinggi","Jika dibanding tidak selisih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151456,"Tekukur digulai lemak","Batang padi dibelah dua","Biarlah kita dimarahkan mak","Asal jadi kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151457,"Tekukur digulai lemak","Batang padi dibelah dua","Biarlah kita dimarahi 'mak","Asal jadi kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151458,"Tekukur digulai lemak","Batang padi dibelah dua","Biarlah adinda dimarahi emak","Asal dapat bersama kanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151459,"Tekukur digulai lemak","Batang padi dibelah dua","Biarlah kita dimarahi emak","Asal jadi kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151460,"Tekukur digulai lemak","Selasih di bawah batang","Sudah lumpur jalannya semak","Sebab kasih beta datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151461,"Tekukurlah denak wira perkasa","Juara peraduan hadiah berganda","Bersyukurlah anak bila dewasa","Betapa pengorbanan ayah bonda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151462,"Tekun cermat mengasah badik","Cantik ulu kayu keruing","Bijak berhemat anak dididik","Kasih perlu disiplin penting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151463,"Tekun kita beramal ibadah","Untuk belanja dikemudian hari","Kita serahkan kehadirat Allah","Mudah-mudahan disyafaatkan Nabi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151464,"Tekun membakar pedang berkarat","Sudah dibakar lalu dikikir","Mata melihat mengambil ibarat","Hati berdetik lalu berfikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151465,"Telaga beriak-riak","Merpati berulang mandi","Kalau tidak mas sepiak","Kerja di mana boleh jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151466,"Telaga mana abang nak mandi","Telaga kecik timba upeh","Tara mana abang nak cari","Tuboh kecik panjang nipih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151467,"Telaga mana adik nak mandi","Telaga batu ayaq selopak","Tara mana adik nak cari","Rambut sepangku pinggang secekak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151468,"Telaga mana hendak mandi","Telaga batu airnya kering","Setara mana hendak dicari","Muka gebu pinggangnya ramping", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151469,"Telage sino aiqna jernih","Bau tekadu sikat gigi","Ape gunante irup sugih","Lamunta sugih isiq kepeng korupsi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151470,"Telah disampaikan kepada ku","Keluarkan sebagian dari hartamu","Tiada tujuan dari semua itu","Melainkan pensucian diri dan hartamu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151471,"Telah kembang bunga teratai","Telah layu bunga kecubung","Kalau niat tak jadi sampai","Lebih baik ku tinggal kampung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151472,"Telah ku coba pergi ke kota","Talam berisi ikan moa","Sudah ku coba menutup mata","Dalam makripat nampak jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151473,"Telah lama abang menyimpai","Pemasak tiang berikut tonggak","Sudah lama abang mengintai","Hendak berterang takut ditolak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151474,"Telah lama kuda berlari","Dikejar budak badan berpeluh","Sudah lama hamba menanti","Kabar tidak pesan pun jauh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151475,"Telah lama kuda berlari","Lidah terjulur matapun sayu","Sudah lama hamba menanti","Habislah umur menahan rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151476,"Telah lama orang berburu","Hendak mencari rusa beranak","Sudah lama abang merindu","Hendak kemari dadaku sesak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151477,"Telah lama orang berlayar","Perahu kolek laju meluncur","Sudah lama abang bersabar","Menunggu adik tak mau menegur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151478,"Telah lama orang memukat","Angin utara sudah berhenti","Sudah lama ku kandung niat","Angin bersua si buah hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151479,"Telah lama orang menekat","Membuat baju kebaya lebar","Sudah lama abang terpikat","Hendak bertemu dada berdebar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151480,"Telah lama orang merenda","Hendak ditekat takut menyalah","Sudah lama abang mencinta","Hendak bercakap tergigit lidah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151481,"Telah masak buah cempedak","Tumbuh lurus dekat perigi","Bangsa tidak emas pun tidak","Apa dipandang kepada kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151482,"Telah masak buah kayu bodi","Jatuh sebiji ke dalam paya","Telah ku coba bertanam budi","Orang enggan apakah daya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151483,"Telah masak buah kepayang","Baru berputik buah mengkudu","Tuan emas kami loyang","Mana mungkin kita bersatu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151484,"Telah masak buah ketaya","Masak sebiji dimakan rubah","Bercerai badan dengan kepala","Namun kasihku tidak berubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151485,"Telah masak buah mengkudu","Anak rusa jatuh tersadung","Daripada hidup menanggung rindu","Baik ku coba mengadu untung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151486,"Telah masak buah mengkudu","Jolok dengan talang seligi","Sungguh sakit menanggung rindu","Sakit tak dapat ditahan lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151487,"Telah masak buah mengkudu","Masak pula buah kepayang","Hati risau bercampur rindu","Siang dan malam terbayang-bayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151488,"Telambah kedai Kina","Orang beradat dalam perahu","Inai persembahan dagang yang hina","Adat berbahasa tiada tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151489,"Telapak tangan boleh menjamah","Tiang tumpuh jadi pegangan","Hubungan baik dengan sasama","Lebih penting dari agama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151490,"Telat bebayar kene dende","Belayar lek UD Teguh","Ketimaq ndek nerek ruan side","Do'a panjatan isik kami nyertain pelungguh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151491,"Telatuk dibelah-belah","Dibelah di atas manggis","Telunjuk diberi Allah","Buat pencolek jari manis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151492,"Telbo maonsur goenong Pasaman","Toejuon darie aches","Adenda niasu kakanda badan","Panton dasuad dangan kartas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151493,"Telepok dibelah-belah","Dengan sebelah di punggung pinggan","Telunjuk kami diberi Allah","Buat pembalut kapur berjintan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151494,"Telepuk di tudung saji","Hendak menyiram ke perigi","Takutkan rakyat Raja Haji","Masuk menyelap di dalam negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151495,"Teloh balam di-dalam dulang","Letak mari dalam mangkok","Siang malam dudok berulang","Kernan sebab buah di-pokok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151496,"Teloh chichak di bawah balay","Anak katak di tepi pintu","Suda bijak bertamba panday","Kenapa boleh orang tipu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151497,"Telok Bagan Telok Chengai","Tiga dengan Telok Kecai","Tengok badan sudah ku kenai","Boleh kita buat selesai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151498,"Telok Bahang cantik lautnya","Anak sungai ada di sana","Ada orang manis mulutnya","Tetapi perangai sangat durjana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151499,"Telok Bahang pandangan indah","Jalan kereta mendaki bukit","Kalau rajin pergi sekolah","Ilmu bertambah serba sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151500,"Telok Bayur pelabuhan kapal","Belanda turun dalam sekochi","Bunga mati kebun tertinggal","Kumbang tidak me-ulang lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151501,"Telok cecak telok bengkarung","Telok biaku labi-labi","Telok satu duene urung","Telok ketiga dek menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151502,"Telur cecak telur bengkarung","Telur penyu labi-labi","Telur satu duanya urung","Telur ketiga tidak menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151503,"Telok Jat jalan ke Jabi","Tempat oghang menjuai sireh","Sekali ku hemat bagai nak jadi","Sekali pulak bagai tak boleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151504,"Telok klara panggong di-hulu","Tempat budak mandi berenang","Susah kita di-tanggong dulu","Bahrulah boleh berjumpa senang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151505,"Telok lingi nampak sebrang","Itong tanjong sampay kan lima","Pokok tinggi berlebat charang","Saya pun gantong bersama sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151506,"Telok sungei di-Kuala Daik","Tempat orang kail gulama","Suka di-semay beneh tak baik","Jikalu kembang tentu tak lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (151507,"Telok Yu Paya Belibeh","Tiga dengan Telok Ewa","Tuboh gebu panjang nipih","Macam mana kita tak gila?", 4, 6);
