INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153183,"Titik air menjadi embun","Gumpal tanah menjadi di gunung","Mulai sedikit asalkan tekun","Cita cita bakal terkabul", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153184,"Titik embun di atas daun","Basahnya jatuh sampai ke tanah","Kasih ibu sampai bertahun","Selama hayat masih di badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153185,"Titiwangsa tamannya indah","Berteduh berehat si warga kota","Pagi pagi bersenam ria","Tubuh sihat minda terbuka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153186,"Titiwangsa tamannya indah","Berteduh berehat si warga kota","Pagi pagi bersenam ria","Tubuh sihat minda terbuka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153187,"Tiuik api panggang gulamo","Kalimuntiang jalan rank ka balai","Mukasuik ati tidak ka lamo","Dek untuang man-jadi lalai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153188,"Tiuik api panggang-lah lokan","Anak rajo pulang man-jalo","Iduik mati di tangan tolan","Tidak sayo duo bicaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153189,"Tiung paksi terbang sekawan","Burung merpati terbang rendah","Dicium nafsi nyatalah perempuan","Di manakan hati abang tak gundah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153190,"Tiup angin berganti-ganti","Nampak bernyala si mercu gunung","Bukan disimpan di dalam hati","Rasa disimpan di dalam jantung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153191,"Tiup angin berganti-ganti","Nampak bernyala Si Mercu Gunung","Bukan disimpan di dalam hati","Rasa disimpan di dalam jantung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153192,"Tiup angin di barat daya","Menuju arah selatan","Kekerasan hanya tipu daya","Orang sembunyikan kelemahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153193,"Tiup angin panggang tilan","Anak raja pulang menjala","Hidup mati di tangan tuan","Abang tidak banyak bicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153194,"Tiup api dari haluan","Buah pepare bertali-tali","Untungku sudah di tangan tuan","Baik juga jadi sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153195,"Tiup api di Gunung Ledang","Habu-habunya kutampi juak","Niat hati matim di bujang","Balu-balunya ku nanti juak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153196,"Tiup api embun bederai","Patah galah haluan perahu","Hajat di hati tak mahu becerai","Kuasa Allah siapa yang tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153197,"Tiup api embun berbalik","Akan pembakar pisang udang","Bantulah adik tolonglah adik","Untuk menarik larangan orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153198,"Tiup api hembus berbalik","Akar pembakar pisang udang","Bantulah adik! Tolonglah adik!","Menarik larangan orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153199,"Tiup api jerang belanga","Akan perebus kepala kurau","Tuan pergi janganlah lama","Siapakah kelak teman bergurau?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153200,"Tiup api jerang belanga","Akan perebus kepala kurau","Tuan pergi janganlah lama","Siapakah kelak teman bergurau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153201,"Tiup api jerang belanga","Hendak menggulai kepala kurau","Abang pergi janganlah lama","Tiada teman hendak bergurau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153202,"Tiup api panggang tilan","Anak raja pulang menjala","Hidup mati di tangan tuan","Adik tidak banyak berbicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153203,"Tiup api panggang tilan","Anak raja pulang menjala","Hidup mati di-tangan tuan","Adek tidak banyak bichara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153204,"Tiup nafiri pukullah nobat","Kerana menyambut ketua negara","Sudikah saudari kita bersahabat","Agar mendapat memadu asmara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153205,"Tiup seruling di tanah lapang","Bunyi merdu mendayu-dayu","Hati runsing kepada abang","Kirimlah salam kepada bayu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153206,"Tiup seruling lagunya lama","Bagailah bunyi buluh perindu","Jikalau rindu akan adinda","Kirimlah salam di angin lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153207,"Tiup-tiup angin di atas","Hendak memetik tali kecapi","Jongnya lilin layarnya kertas","Hendak menempuh lautan api", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153208,"Tjantiek tarinanj gantoeng","Tempayan kosong beri-isie niela","Tidoer sekedjap mengiempie datang","Bayang-bayang koe kiera dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153209,"Tjinta itoe soetji na boeta","Orangnja sadja toeling mangraki","Saling memandang aroeppa mata","Mata beradoe si-kaeroki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153210,"tjukup pengalamen delenge kurasa","urum luhni mata mudjaril rembebe","meh nge kuarung kampung rum kota","ken tene mata ken nipi nome", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153211,"Tobang sakawan burung balibi","Ka dalam bonca hinggok baronti","Di buek patona urang mangaji","Baro jahonam tunangan nanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153212,"Tobang tobu tasindayung","Tasindayung ka Malako","Tuntuik ilemukan ganti payung","Payung pendendeng api nuroko", 4, 6);
