INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153214,"Tobang tobu tinggi-tinggi","Samo tinggi dengan durian","Tuntuik ilemu maso kini","Jangan menyosal kamudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153215,"Tobang tobu tinggi-tinggi","Tobang rumbio tapangkal-pangkal","Tuntuik ilemu maso kini","Iduik di dunio indak kokal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153216,"Tobang tobu tinggi-tinggi","Tobang rumblop tapangkal-pangkal","Tuntuik ilemu maso kini","Iduik di dunio indak kokal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153217,"Todak banyak di Laut Pasir","Tuman dipegang jatuh ke laut","Tidak badan jadi taksir","Iman pun tetap sehingga maut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153218,"Todak bernama batang semilu","Tukang melarik tali rotan","Kalu musim bua tak tentu","Habis gugor di tengah medan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153219,"Todak menyerang Singapura","Habis dikerat dicincang lumat","Bila khianat pada manusia","Dunia akhirat tak selamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153220,"Todak menyerang Singapura","Habis dikerat dicincang lumat","Bila khianat pada manusia","Dunia akhirat tak selamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153221,"Toie sa koe toie pa waoe","Be tjie toie koe maranda teimbe","Toie ma patoepa","Ma doende gomie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153222,"Tok Andak menunggu chempedak","Dari senja hingga ka-suboh","Anjing menyalak babi bertindak","Mengapa harimau berpelok tuboh?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153223,"Tok bandar jalan di lotong","Katiga jangan batu di paya","Tarek napas kunang kan untong","Untong nya tidak sarupa saya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153224,"Tok Haji berserban merah","Turun ke padang berburu rusa","Orang mengaji memuji Allah","Orang sembahyang mencuci dosa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153225,"Tok haji membawa halia","Hendak di-jual ka-parit buntar","Sebab yang keji jadi mulia","Kerana manusia dapat di-putar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153226,"Tok Japaq kandaq dapoq","Anak katak bejari-jari","Apa habaq oghang di dapoq","Sudah masak angkat ke mari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153227,"Tok lebai jatoh parit","Tok aji jatoh kubang","Tok lebai dok wirid","Tok aji dok sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153228,"Tok unku ka telok piay","Gulai kli tidak berlada","Niat hati berkapan chinday","Suda terbli kasa belanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153229,"Tok unku ka telok piay","Gulai kli tidak berlada","Niat hati berkapan chinday","Suda terbli kasa belanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153230,"Tokek lorek jejer loro","Kucing luring tukang ndhepidhepi","Elek retek goleka bojo","Mbesuk lek gering enek sing mijeti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153231,"Tokek belang berjajar dua","Kucing luring suka mengintip","Istri jelek tak jadi apa","Jika sakit ada yang mijit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153232,"Toki-toki dinding","Dinding gaba-gaba","Nene' tidor mircing","Tete' raba-raba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153233,"Mengetuk-ngetuk dinding","Dinding (terbuat dari) kayu gabah","Nenek tidurnya miring","Kakek meraba-raba (menggerayang)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153234,"Toko cina banyak langganan","Masuk keluar membeli barang","Cita-cita jadi majikan","Sampai tua hanya pelayan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153235,"Tokol momot manto felem","Felem ne felem Bruce lee","Ndeq lupaq bareh kelem","Anti leq julun bale", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153236,"Tokol-toko leq bebaleq","Ngelorang gerupaq lendong sampi","Kedatengan side ku badeq","Maraq idapku siq ngimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153237,"Tokol-tokol le atas aton","Sue-sue polak jelika na","Pada pacu ntan semeton","Senga' dunia wah seda rua na", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153238,"Tokol-tokol lek sekolah","Sambil nginem aiq tuaq","Pade gaweq pehawean siq solah","Ndak gawek pegawean siq salaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153239,"Tokol-tokol leq atas kursi","Pinaq rokok kadu mako","Endaq girang pada korupsi","Lamun mele endeq te demo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153240,"Tokol-tokol leq atas pelangkan","Bareng-bareng kence pak haji","Endak aseq mikirin perpisahan","Sengaq wah jari ketentuan nenek kaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153241,"Tokol-tokol leq bawaq nangke beloto","Ndeq lupa' bau buaq ne","Lamun te berpisah ndeq nangis berjelo-jelo","Sengaq asek ndeq ne arak kenene", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153242,"Tokol-tokol leq berugak","Selalok te manto dengan menari","Uah ngonek ndek te saling gitak","Mare-mare bedait leq balen Meri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153243,"Tokol-tokol leq sedin gabah","Ndak lupa ngaken bubur","Pacu-pacu gaweq ibadah","Adeqne endeq nyesel leq dalem kubur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153244,"Tokol-tokol leq sedin taman","Sambil kaken jaj maiq rasana","Solah lengena aten dengan","Bau tegitaq lengan pegaweanna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153245,"Tokol-tokol leq sedin telaga","Ndaq lupaq jauq kursi","Berembe jari kanaq-kanaq bangsa","Lamun saq toaq lueq korupsi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153246,"Tokol-tokol leq sedin telage","Ngitaq empak saling kejer","Lamun mele ngraih cite-cite","Pacu-pacu isiq belajar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153247,"Tokol-tokol maen catur","Pinaq api isiq colok","Lamun de mele lueq batur","Endak girang pada okok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153248,"Tokol-tokol maraq dengan susah","Kepeng tetagih bilang taon","Baruq sebulan tebepisah","Mareq idepne wah setaon", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153249,"Tokol-tokol sampiq bejait","Bejait pinag kereng solah","Nengke malik ke bedait","Tegehene suwe te berpisah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153250,"Tokol-tokol sompiq bebace","Leq sedin kekayuq taoq te nyenyandar","Lamun te bekedeq ndeq te ece","Sengaq berpisah jemak taoq te sadar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153251,"Tolak ber-gala jalan meliti","Arus ber-gilay pinda ke-s'brang","Jangan sala kurang tau arti","Bunga tak bolay tumbu di karang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153252,"Tolak perahu ambil perahu","Sayang nanggalah terjungkir-jungkir","Tolak mahu diambit mahu","Kami tiada sifat pemungkir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153253,"Tolang duren sekat epe tioq","Tiang talet leq tengaq lelendang","Wah lime taon te bareng leq sekeq taoq","Laguq nengke side wah lalo telang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153254,"Tolang kacang tolang lebui","Ta kerem isiq aik","Tiang lalo semeton jari","Mudah-mudahan ta bedait malik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153255,"Tolong serapah orang di hulu","Orang di hulu kampung nan lama","Ke mana sumpah kita dahulu","Ikrar setuju hidup bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153256,"Tolong serapah orang di hulu","Orang di hulu kampung nan lama","Ke mana sumpah kita dahulu","Ikrar setuju hidup bersama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153257,"Toloq paoq atas sempare","Munte mele karing te beit","Timaqne jaoq sedin segare","Lamune jodoh gente bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153258,"Toloq sikat leq kolon ketujur","Bece Koran leq keket gedeng","Mule sekat temilen dengan jujur","Sengak moral betuker isiq kepeng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153259,"Tolo-tolo tiang amang","Tolo-tolo gadapang","Nangkon mangan ihan amang","Asal butong mangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153260,"Tomato kicap bawa berjaja","Berjaja sampai ke Sungai Besi","Kurang cakap banyak kerja","Batas cantik ubi berisi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153261,"Tombak brambu buatan Daik","Bawak orang ka negri China","Kalu bangsa kayu yang baik","Bagimana arang bergitu abu nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153262,"Tombak permainan Indera Sakti","Kuda dipacu di tengah medan","Baik pikirkan di dalam hati","Di situlah tempat menyenangkan badan", 4, 6);
