INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153265,"Tondik-tondikan siadosanmu di toru on","Surduon tolong salendang tu toru on","Anso adong ulos simanare on","Na lomlom dia do giot mu i toru on", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153266,"Tong jalutong","Kambayau bawah batang","Ambung anak tutong","Mangayau inda datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153267,"Tong kosong berbunyi nyaring","Isi air bunyinya redam","Otak kosong omongnya tinggi","Lebih aman jadi pendiam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153268,"Tong kosong nyaring bunyinya","Banyak khayal tapi tak mampu","Orang yang sombong banyak bicaranya","Harus waspada banyak ditipu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (153269,"Tong nan kosong nyariang bunyinyo","Karano inyo indak barisi","Urang nan bodoh gadang otanyo","Kalau nan pandai awak sandiri", 4, 6);
