INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154342,"Urang melukah menikalak","Tiada rotan ketaya ada","Orang bersumpah selagi galak","Tiada orang lagi percaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154343,"Urang nan takuik pado lahia","Batin salalu bakarupatan","Hutang nan lansai tak jo bayia","Bagak nan indak jo paretongan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154344,"Urang Nata manjua panah","Pamanah buruang di udaro","Sajak sajangka dari tanah","Balunlah tau nan bak nangko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154345,"Urang Padang mandi ka gurun","Mandi bakusuak bungo palo","Hari petang matoari turun","Dagang baurai aie mato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154346,"Urang Padang mandi ka Gurun","Mandi bakusuak si bungo palo","Hari patang matohari turun","Dagang baurai si aia mato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154347,"Urang Padang mandi ka gurun","Mandi basiram bungo layo","Ari patang matoari turun","Dagang baur ai ala mato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154348,"Urang Pauah pai ka Padang","Tibo di Padang jua-bali","Dari jauah kamilah datang","Mandanga adiak baiak budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154349,"Orang Pauh pergi ke Padang","Tiba di Padang berjual-beli","Dari jauh kamilah datang","Mendengar adik baik budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154350,"Urang Sianok pandai lukah","Kanailah udang duo-duo","Kok lai urang mati disumpah","Saurang sajo urang tak kabasuo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154351,"Orang Sianok pandai melukah","Kenalah udang dua-dua","Jika ada orang mati disumpah","Tak seorang pun akan tersua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154352,"Urang Silungkang mambawok kapeh","Urang Tilatang mambawok aia","Nan mancancang nan mamampeh","Nan bautang nan mambayia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154353,"Urang Suliki manyuligi","Urang hanyuik baduo-duo","Kasiah nan jangan diputuihi","Bagi rambuik tinggakan juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154354,"Orang Suliki pergi menyuligi","Orang hanyut berdua-dua","Kasih jangan diputusi","Separuh rambut tinggalkan jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154355,"Urang Talu manjua rotan","Urang Tiagan manjua bungo","Sasa dahulu pandapatan","Sasa kamudian indak baguno", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154356,"Uras kelemeq lampak sekolah","Ndeq lupeq sosok gigi","Nunde mele arande solah","Ndeq pede milu korupsi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154357,"Uras tindoq wah liwat benar","Terus mandiq leq pancoran","Nda' alurang diri kesasar","Peta dengan tao' beketoan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154358,"Urat kacang bertali-tali","Belum diraba belum kan layu","Kuat orang jangan perduli","Marah itu ada tempatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154359,"Urat kacang bertali-tali","Belum diraba belumkan layu","Kuat orang jangan peduli","Belum dicuba belum kan tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154360,"Urat kayu tengah halaman","Bila terbelah menjadi layu","Adat Melayu zaman berzaman","Bila tersalah berilah tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154361,"Urat mana yang kami rentang","Urat kayu tengah halaman","Adat mana yang kami bentang","Adat Melayu zaman berzaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154362,"Urat sirih nampak berajah","Bercabang tiga jarak setara","Terima kasih Puan Khadijah","AO juga selamat sejahtera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154363,"Uratnya songsang pohon halban","Mari ditanam pohon rumbia","Mengharapkan musang menjaga reban","Kononnya ayam hidup bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154364,"Urek jilatang rang panto-kan","Gambulau daun aleh dulang","Tiok urang datang di-tanyo-kan","Di junguik pulau kato urang (Pantun Minangkabau) (L K Harmsen) (Batavia) (Tahun 1875)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154365,"Urirang merah-nya dadu","Jatoh dilumpor merah-nya kurang","Sakit sunggoh badan dimadu","Masok disumsun menembus tulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154366,"Uroh uroh di matan suraoh","Suraoh melarek di kaki dulang","Ombak bertepok di ujong puloh","Aros nya kras bukan kepalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154367,"Urum2 beluh musara loloten","Urum2 mowen musara tenamunen","Tape gere kusangka wo bunge gergemen","Beluh gere bersinen ne kin sitaring", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154368,"Usah ba-ladang mudiek Pauah","Rakik siapa man-jala-kan","Usah ba-dagang jauah-jauah","Sakik siapa mang-gilo-kan (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154369,"Usah diacah si anak kera","Nanti diserkup si bapa beruk","Pecah tak pecah saya tak kira","Takut meletup telur kemuruk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154370,"Usah dikerat batangnya joran","Biarkan sama elok sempurna","Buta isyarat ingkar aturan","Sebab utama berlaku bencana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154371,"Usah ditahan tabiang runtuah","Kalau ditahan ditimponyo","Usah ditahan ati rusuah","Kalau ditahan digilonya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154372,"Usah ditahan tebing runtuh","Kalau ditahan akan ditimpanya","Usah ditahan hati rusuh","Kalau ditahan gila dibuatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154373,"Usah ditebang pohonan rimbun","Naungan kebun bertambah cantik","Kalau berkembang di balik daun","Di mana embun dapat menitik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154374,"Usah di-timbo sumua Padang","Jikok di-timbo mangkin panuah","Usah di-cinto anak dagang","Jikok di-cinto mangkin jauah (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154375,"Usah ditiru bungo culan","Bungo nan harum tak jadi buah","Kaduo kuek maamalkan","Alemu nan ado bapaidah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154376,"Usah kito duduak di majun","Mari kito duduak ka tangah","Usah kito rintang di pantun","Mari kito pulang ka rumah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154377,"Usah kita duduk di majun","Mari kita duduk ke tengah","Usah kita rintang kan pantun","Mari kita pulang ke rumah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154378,"Usah lai diarok lai","Aroklah di gungguang pikau","Usah lai diarok lai","Sayo di baliak gunung ijau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154379,"Usahlah diharap lagi","Harapanlah digunggung pikau","Usahlah diharap lagi","Saya di balik gunung hijau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154380,"Usah menuba di tengah hari","Patah kemudi diganti galah","Usah beriba meratap diri","Berserah diri Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154381,"Usah puspa merndah diri","Kerana cinta sudah bertakhta","Usah taulan menunding jari","Sedangkan kita punya sengketa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154382,"Usah takaja jo talalah","Rancak jo tanang mak nyo lalu","Macam kaduo banamo 'pangalah'","Maracik anak buah tak piliah bulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154383,"Usah taleo-leo padi","Namun bidauik saampu juo","Usah baibo-ibo ati","Namun iduik batamu juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154384,"Usah terjela jela padi","Namun biduk seempu jua","Usah berhiba-hiba hati","Jika hidup bertemu jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154385,"Usahakan berkata-kata","Yang tulus dari hati","Masih ada cita-cita","Kegagalan tidak berarti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154386,"Usahkan menyimpai kepala tiang","Simpai galah abang simpaikan","Usahlah mengungkai di tangan orang","Di tangan Allah abang ungkaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154387,"Usahkan pinang di parit","Lagi sabut saya torang","Usahkan bintang di langit","Lagi rambut saya bilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154388,"Usahokan budi tak kalihatan","Saumua hiduik urang tak lupo","Budi kok tajua handak takuikkan","Paham tagadai rusuahkan pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154389,"Usang bukan sebarang usang","Usang mangkuk gunanya sama","Pulang bukan sebarang pulang","Pulang menengok janda lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154390,"Usang bukan sebarang usang","Usang mangkuk gunanya sama","Pulang bukan sebrang pulang","Pulang menengok janda lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154391,"Usang bukan sebarang usang","Usungan berisi buah belera","Untung bukan sebarang untung","Untung berbini lepas selera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154392,"Usang istana tidak dihuni","Penuh balai bertikar pandan","Hutang di sana langsari di sini","Kain sehelai lusuh di badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154393,"Usap bukan sebarang usap","Mengusap kaki sambil bergaru","Sedap bukan sebarang sedap","Sedap menjadi pengantin baru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154394,"Usap bukan sebarang usap","Mengusap kaki sambil bergaru","Sedap bukan sebarang sedap","Sedap menjadi pengantin baru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154395,"Usap bukan sebarang usap","Mengusap muka supaya tenang","Sedap bukan sebarang sedap","Sedap meraba anak dara orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154396,"Usap bukan sebarang usap","Mengusap muka supaya tenang","Sedap bukan sebarang sedap","Sedap meraba anak dara orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154397,"Usman Awang raja puisi","Anak jati Kuala Sedili","Kaya wang cantik berkongsi","Menabur bakti banyak sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154398,"Usman Awang seoghang penuleh","Sudah pi meninggal dunia","Usman Awang oghang terpileh","Cerita dia membawa bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154399,"Usu Nong memilih rempah","Pakei membuat si bubur pedas","Jangan termenung janganlah gundah","Kamek berniat jujur dan ikhlas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154400,"Usung bukan sebarang usung","Usungan berisi buah belera","Untung bukan sebarang untung","Untung berbini lepas selera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154401,"Utamayang hukum singene pasal","Indonesia pasti jaye","tanggal seke' bulan sawal","Dengan Islam hari reye", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154402,"Utang kaduo sapanjang adat","Manampuah jalan nan pasa","Jalan dunia jalan akhirat","Bakato luruih bajalan bana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154403,"Utang rata tempat","Semua tidak bayaq","Oghang benci meluat","Tidak tanya habaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154404,"Utang sana sini","Tidak boleh bayaq","Duit beguni-guni","Ke mana dia lempaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154405,"Utang sana sini","Tidak mahu bayaq","Sembang sana sini","Macam kerja loyaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154406,"Utara berdengong tana olanda","Kapal menyongsong berlambongan","Antara blom porak pranda","Di tutop layar ber-lindongan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154407,"Utar-utar tiram tergantung","Tempayan kosong berisi nila","Tidur sekejap mengimpi datang","Bayang bayang ku sangkan dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154408,"Utas-utas di atas pintu","Pakaian jauh baharu dapat","Orang sukar sahaja begitu","Membuang nyawa baharulah dapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154409,"Utih'.adik utih","Mandi dalam pasu","Penat adik bertatih","Kakak upah susu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154410,"Utih'adik utih","Duduk pelan-pelan","Rajin adik bertatih","Lekas pandai jalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154411,"Utih'adik utih","Pakai baju belang","Tatih adik'tatih","Tatih ulang-ulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154412,"Utik-utik ikan bimbang","Minyak ketiau di ujung galah","Raja kecil silakan pulang","Linggi di langgar Riau yang salah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154413,"Utuk-utik ikan bambang","Minyak ketiau di hujung galah","Raja Kecil silakan pulang","Linggi diserang Riau yang alah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154414,"Utusan raja di negri sa-bua","Di ramay kan rayat umat negri","Bua nya masak bijik pun tua","Benay ada bertunas lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154415,"Uuh uuh aah uuh uuh aah aah","Suara beruk dalam hutan","Di puncak kenikmatan","Orang kembali menjadi kera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154416,"Uwah bagawean awakku lelah","Nginem kupi ngadu gelas","Baeh uleq sekolah","Anti aku leq julun kelas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154417,"Uwit jeruk amba godhonge","Wohe mateng dipethik bocah","Aja umuk yen lagi duwe","Saiki seneng sesuke susah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154418,"Wafatnya Baginda jadi sejarah","Berderai air mata Aisyah","Berkesan sungguh tiada ter surah","Bergaul mesra tak pernah resah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154419,"Wafatnya baginda sudah pula lama","Berderai air mata Aisyah","Sungguh mulia tiada ter surah","Bergaul mesra berakhlakul karimah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154420,"Wah biyan lalo tipaq Labuhan","Terus jemaq lalo tipaq Senggigi","Jaman niki sulit beboyaq gawean","Pejabat korupsi rakyat siq rugi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154421,"Wahai anak yang setiawan","Engkau ini anak perempuan","Jikalau engkau ada pertemuan","Suamimu itu jangan di lawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154422,"Wahai ananda belahan diri","Kerja menyalah jangan hampiri","Berbuat maksiat jangan sekali","Supaya hidupmu diberkahi Illahi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154423,"Wahai ananda buah hati bunda","Berpegang teguhlah pada agama","Beramalllah engkau sehabis daya","Supaya selamat dari neraka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154424,"Wahai ananda cahaya gemilang","Ilmu di dada jangan dibuang","Jauhkan segala pantang dan larang","Supaya hidupmu tidak terbuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154425,"Wahai ananda cahaya mata","Janganlah tamak kepada harta","Mencari nafkah berpada-pada","Supaya hidupmu tiada ternista", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154426,"Wahai ananda cahaya rumah","Dalam ibadah janganlah lengah","Kerjakan suruh jauhkan cegah","Supaya hidupmu beroleh berkah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154427,"Wahai ananda dengarkan peri","Tunangan hidup adalah mati","Carilah bekal ketika pagi","Supaya tidak menyesal nanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154428,"Wahai ananda dengarlah madah","Baikkan laku elokkan tingkah","Banyakkan kerja yang berfedah","Supaya hidupmu beroleh berkah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154429,"Wahai ananda dengarlah pesan","Kuatkan hati teguhkan iman","Jangan didengar bisikan setan","Supaya dirimu diampuni Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154430,"Wahai ananda harapan ibu","Dalam beragama jangalah ragu","Kokohkan iman perdalam ilmu","Semoga Allah akan membantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (154431,"Wahai ananda hendaklah ingat","Hidup di dunia amatlah singkat","Banyakkan amal serta ibadat","Supaya selamat dunia akhirat", 4, 6);
